## ‚úÖ –ü—Ä–∏–º–µ—Ä –∫–æ–¥–∞: –ú–∏–Ω–∏-–û–° –Ω–∞ C

```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_TASKS 10

typedef enum {
    TASK_READY,
    TASK_RUNNING,
    TASK_SUSPENDED,
    TASK_TERMINATED
} TaskState;

typedef struct {
    char name[32];
    void (*func)(void);
    int priority;
    TaskState state;
    int stack_size;
    void *stack;
} Task_t;

Task_t tasks[MAX_TASKS];
int task_count = 0;

// –§—É–Ω–∫—Ü–∏—è —Å–æ–∑–¥–∞–Ω–∏—è –∑–∞–¥–∞—á–∏
void create_task(char *name, void (*func)(void), int priority, int stack_size) {
    if (task_count >= MAX_TASKS) {
        printf("–û—à–∏–±–∫–∞: –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–¥–∞—á –¥–æ—Å—Ç–∏–≥–Ω—É—Ç–æ.\n");
        return;
    }

    Task_t *new_task = &tasks[task_count];
    strcpy(new_task->name, name);
    new_task->func = func;
    new_task->priority = priority;
    new_task->state = TASK_READY;
    new_task->stack_size = stack_size;
    new_task->stack = malloc(stack_size);

    if (!new_task->stack) {
        printf("–û—à–∏–±–∫–∞: –Ω–µ —Ö–≤–∞—Ç–∞–µ—Ç –ø–∞–º—è—Ç–∏ –¥–ª—è —Å—Ç–µ–∫–∞ –∑–∞–¥–∞—á–∏.\n");
        return;
    }

    task_count++;
}

// –§—É–Ω–∫—Ü–∏—è –ø–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è –∑–∞–¥–∞—á
void scheduler() {
    int i;
    for (i = 0; i < MAX_TASKS; i++) {
        if (tasks[i].state == TASK_READY) {
            // –ó–∞–ø—É—Å–∫ –∑–∞–¥–∞—á–∏
            printf("–ó–∞–ø—É—Å–∫ –∑–∞–¥–∞—á–∏: %s\n", tasks[i].name);
            tasks[i].state = TASK_RUNNING;
            tasks[i].func();
            tasks[i].state = TASK_TERMINATED;
            break;
        }
    }
}

// –§—É–Ω–∫—Ü–∏—è —É—Å—Ç—É–ø–∫–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è
void yield() {
    scheduler();
}

// –ü—Ä–∏–º–µ—Ä –∑–∞–¥–∞—á
void task1(void) {
    printf("–ó–∞–¥–∞—á–∞ 1 –≤—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è...\n");
    yield();
    printf("–ó–∞–¥–∞—á–∞ 1 –∑–∞–≤–µ—Ä—à–µ–Ω–∞.\n");
}

void task2(void) {
    printf("–ó–∞–¥–∞—á–∞ 2 –≤—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è...\n");
    yield();
    printf("–ó–∞–¥–∞—á–∞ 2 –∑–∞–≤–µ—Ä—à–µ–Ω–∞.\n");
}

void task3(void) {
    printf("–ó–∞–¥–∞—á–∞ 3 –≤—ã–ø–æ–ª–Ω—è–µ—Ç—Å—è...\n");
    yield();
    printf("–ó–∞–¥–∞—á–∞ 3 –∑–∞–≤–µ—Ä—à–µ–Ω–∞.\n");
}

// –§—É–Ω–∫—Ü–∏—è –∑–∞–ø—É—Å–∫–∞ –û–°
void start_os() {
    create_task("Task1", task1, 1, 1024);
    create_task("Task2", task2, 2, 1024);
    create_task("Task3", task3, 3, 1024);

    while (1) {
        scheduler();
    }
}

int main() {
    start_os();
    return 0;
}
```

