#include <ap_fixed.h>
#include <hls_math.h>
#include <hls_stream.h>

// –ü–∞—Ä–∞–º–µ—Ç—Ä—ã
#define N 1024 // –†–∞–∑–º–µ—Ä FFT
#define DATA_WIDTH 32 // –®–∏—Ä–∏–Ω–∞ –¥–∞–Ω–Ω—ã—Ö (–≤ –±–∏—Ç–∞—Ö)
#define FRACTIONAL_WIDTH 30 // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥—Ä–æ–±–Ω—ã—Ö –±–∏—Ç–æ–≤

typedef ap_fixed<DATA_WIDTH, FRACTIONAL_WIDTH> data_t;

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≤—ã—á–∏—Å–ª–µ–Ω–∏—è FFT
void fft(data_t input[N], data_t output[N]) {
    #pragma HLS INTERFACE s_axilite port=input bundle=control
    #pragma HLS INTERFACE s_axilite port=output bundle=control
    #pragma HLS INTERFACE s_axilite port=return bundle=control

    // –í–Ω—É—Ç—Ä–µ–Ω–Ω–∏–µ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ
    data_t temp[N];
    #pragma HLS ARRAY_PARTITION variable=temp cyclic factor=2

    // –ü–µ—Ä–µ–º–µ–Ω–Ω—ã–µ –¥–ª—è —Ä–µ–∫—É—Ä—Å–∏–∏
    int n = N;
    int m = log2(n); // –õ–æ–≥–∞—Ä–∏—Ñ–º –ø–æ –æ—Å–Ω–æ–≤–∞–Ω–∏—é 2

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π FFT
    for (int i = 0; i < m; i++) {
        #pragma HLS UNROLL
        int j = 1 << i;
        int k = 1 << (m - i - 1);
        data_t w_real = cos(2 * M_PI / (2 * j));
        data_t w_imag = sin(2 * M_PI / (2 * j));

        for (int l = 0; l < n; l += 2 * j) {
            for (int m = 0; m < j; m++) {
                data_t t_real = input[l + m] * w_real - input[l + j + m] * w_imag;
                data_t t_imag = input[l + m] * w_imag + input[l + j + m] * w_real;

                temp[m] = input[l + m];
                input[l + m] = temp[m] + t_real;
                input[l + j + m] = temp[m] - t_real;
                temp[m] = input[l + m];
                input[l + m] = temp[m] + t_imag;
                input[l + j + m] = temp[m] - t_imag;
            }
        }
    }

    // –°–±–æ—Ä —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
    for (int i = 0; i < N; i++) {
        output[i] = input[i];
    }
}
```

