## ‚úÖ –ö–æ–¥ –Ω–∞ C —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º LLVM-C API

```c
#include <stdio.h>
#include <llvm-c/Core.h>
#include <llvm-c/Analysis.h>
#include <llvm-c/Target.h>
#include <llvm-c/Transforms/Scalar.h>

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —Ñ—É–Ω–∫—Ü–∏–∏ fib(n)
void create_fib_function(LLVMModuleRef module) {
    // –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Ç–∏–ø–∞ —Ñ—É–Ω–∫—Ü–∏–∏: int fib(int n)
    LLVMTypeRef int_type = LLVMInt32Type();
    LLVMTypeRef param_type = LLVMPointerType(int_type, 0);
    LLVMTypeRef func_type = LLVMFunctionType(int_type, &param_type, 1, 0);

    // –°–æ–∑–¥–∞–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏ fib –≤ –º–æ–¥—É–ª–µ
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", func_type);

    // –ü–æ–ª—É—á–µ–Ω–∏–µ –±–ª–æ–∫–∞ entry
    LLVMBuilderRef builder = LLVMCreateBuilder();
    LLVMSetInsertionBB(builder, LLVMGetEntryBasicBlock(fib_func));

    // –ü–æ–ª—É—á–µ–Ω–∏–µ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞ n
    LLVMValueRef n = LLVMGetParam(fib_func, 0);

    // –ë–∞–∑–æ–≤—ã–µ —Å–ª—É—á–∞–∏: n == 0 –∏–ª–∏ n == 1
    LLVMValueRef zero = LLVMConstInt(int_type, 0, 0);
    LLVMValueRef one = LLVMConstInt(int_type, 1, 0);

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ n == 0
    LLVMValueRef cond0 = LLVMBuildICmp(builder, LLVMIntEQ, n, zero, "cond0");
    LLVMValueRef ret0 = LLVMBuildReturn(builder, zero, "");

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ n == 1
    LLVMValueRef cond1 = LLVMBuildICmp(builder, LLVMIntEQ, n, one, "cond1");
    LLVMValueRef ret1 = LLVMBuildReturn(builder, one, "");

    // –°–æ–∑–¥–∞–Ω–∏–µ –±–ª–æ–∫–∞ –¥–ª—è —Ä–µ–∫—É—Ä—Å–∏–∏
    LLVMBasicBlockRef then_block = LLVMAppendBasicBlock(fib_func, "then");
    LLVMBasicBlockRef else_block = LLVMAppendBasicBlock(fib_func, "else");
    LLVMBasicBlockRef merge_block = LLVMAppendBasicBlock(fib_func, "merge");

    // –í–µ—Ç–≤–ª–µ–Ω–∏–µ: –µ—Å–ª–∏ n == 0 ‚Üí return 0
    LLVMBuildCondBr(builder, cond0, then_block, else_block);

    // –ë–ª–æ–∫ then: n == 0
    LLVMSetInsertionBB(builder, then_block);
    LLVMBuildBr(builder, merge_block);

    // –ë–ª–æ–∫ else: n != 0
    LLVMSetInsertionBB(builder, else_block);

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ n == 1
    LLVMBuildCondBr(builder, cond1, then_block, else_block);

    // –ë–ª–æ–∫ else: n != 0 –∏ n != 1
    LLVMSetInsertionBB(builder, else_block);

    // –í—ã–∑–æ–≤ fib(n-1)
    LLVMValueRef n_minus_1 = LLVMBuildSub(builder, n, one, "n_minus_1");
    LLVMValueRef call1 = LLVMBuildCall(builder, fib_func, &n_minus_1, 1, "");

    // –í—ã–∑–æ–≤ fib(n-2)
    LLVMValueRef n_minus_2 = LLVMBuildSub(builder, n_minus_1, one, "n_minus_2");
    LLVMValueRef call2 = LLVMBuildCall(builder, fib_func, &n_minus_2, 1, "");

    // –°–ª–æ–∂–µ–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
    LLVMValueRef sum = LLVMBuildAdd(builder, call1, call2, "sum");

    // –í–æ–∑–≤—Ä–∞—Ç —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
    LLVMBuildRet(builder, sum);

    // –ë–ª–æ–∫ merge
    LLVMSetInsertionBB(builder, merge_block);
    LLVMBuildBr(builder, merge_block);

    // –í–æ–∑–≤—Ä–∞—Ç –∏–∑ —Ñ—É–Ω–∫—Ü–∏–∏
    LLVMSetInsertionBB(builder, merge_block);
    LLVMBuildRet(builder, zero); // –≠—Ç–æ –Ω–µ –Ω—É–∂–Ω–æ, –Ω–æ –¥–ª—è –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è

    // –£–¥–∞–ª–µ–Ω–∏–µ –ø–æ—Å—Ç—Ä–æ–∏—Ç–µ–ª—è
    LLVMDisposeBuilder(builder);
}

int main() {
    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è LLVM
    LLVMInitializeNativeTarget();
    LLVMInitializeNativeTargetAsmPrinter();
    LLVMInitializeNativeDisassembler();

    // –°–æ–∑–¥–∞–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞
    LLVMContextRef context = LLVMGetGlobalContext();

    // –°–æ–∑–¥–∞–Ω–∏–µ –º–æ–¥—É–ª—è
    LLVMModuleRef module = LLVMModuleCreateWithName("fib_module");

