                else:
                    print("–û—à–∏–±–∫–∞: —Å—á–µ—Ç –Ω–µ –Ω–∞–π–¥–µ–Ω.")

            elif choice == "3":
                acc_num = input("–í–≤–µ–¥–∏—Ç–µ –Ω–æ–º–µ—Ä —Å—á–µ—Ç–∞: ")
                account = self.get_account(acc_num)
