#include <npp_plugin.h>
#include <Scintilla.h>
#include <ScintillaMessages.h>
#include <string>
#include <vector>

// –ò–º—è –ø–ª–∞–≥–∏–Ω–∞
const TCHAR *pluginName = TEXT("RemoveSpacesOnPaste");
const TCHAR *pluginMenuName = TEXT("Remove Spaces on Paste");

// –§—É–Ω–∫—Ü–∏—è-–æ–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–æ–±—ã—Ç–∏—è –≤—Å—Ç–∞–≤–∫–∏
void CALLBACK OnPaste(NppData notepadPlusData, int scintillaIndex) {
    Scintilla::IScintillaPtr sci = notepadPlusData._scintilla;
    if (!sci) return;

    // –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—É—â–∏–π –∫—É—Ä—Å–æ—Ä
    int pos = sci->SendMessage(SCI_GETCURRENTPOS, 0, 0);
    int anchor = sci->SendMessage(SCI_GETANCHOR, 0, 0);

    // –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—Å—Ç –∏–∑ –±—É—Ñ–µ—Ä–∞ –æ–±–º–µ–Ω–∞
    std::string clipboardText;
    if (OpenClipboard(nullptr)) {
        HANDLE hGlobal = GetClipboardData(CF_TEXT);
        if (hGlobal) {
            char *text = static_cast<char*>(GlobalLock(hGlobal));
            if (text) {
                clipboardText = text;
                GlobalUnlock(hGlobal);
            }
        }
        CloseClipboard();
    }

    // –û—á–∏—â–∞–µ–º —Ç–µ–∫—Å—Ç –æ—Ç –ø—Ä–æ–±–µ–ª–æ–≤
    std::string cleanedText;
    for (char c : clipboardText) {
        if (!isspace(c)) {
            cleanedText += c;
        }
    }

    // –í—Å—Ç–∞–≤–ª—è–µ–º –æ—á–∏—â–µ–Ω–Ω—ã–π —Ç–µ–∫—Å—Ç
    sci->SendMessage(SCI_REPLACESEL, 0, reinterpret_cast<LPARAM>(cleanedText.c_str()));
    sci->SendMessage(SCI_SETCURRENTPOS, pos, 0);
    sci->SendMessage(SCI_SETANCHOR, anchor, 0);
}

// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–æ–æ–±—â–µ–Ω–∏–π –æ—Ç Notepad++
void CALLBACK PluginMessageProc(HWND hwnd, int message, WPARAM wParam, LPARAM lParam) {
    if (message == NPPM_MESSAGE_TOPLUG) {
        if (wParam == NPPM_MSG_PASTE) {
            NppData nppData = *(NppData*)lParam;
            OnPaste(nppData, 0); // 0 –¥–ª—è –≥–ª–∞–≤–Ω–æ–≥–æ –æ–∫–Ω–∞
        }
    }
}

