```c
#include "hls_stream.h"
#include "ap_int.h"
#include "ap_fixed.h"
#include "xf_complex.hpp"
#include "xf_dsp/fft/fft.hpp"

// –ü–∞—Ä–∞–º–µ—Ç—Ä—ã FFT
#define FFT_SIZE 1024
#define DATA_WIDTH 16
#define SCALE_MODE XF_FFT_SCALE_RDBK
#define TWIDDLE_TABLE XF_FFT_TWIDDLE_TABLE
#define OUTPUT_ORDER XF_FFT_OUTPUT_ORDER_NATURAL
#define INPUT_ORDER XF_FFT_INPUT_ORDER_NATURAL
#define DATA_TYPE XF_FFT_DATA_TYPE_CX
#define TWIDDLE_TYPE XF_FFT_TWIDDLE_TYPE_CX
#define SCALE_TYPE XF_FFT_SCALE_TYPE_RDBK
#define FFT_TYPE XF_FFT_TYPE_R2C
#define FFT_DIR XF_FFT_DIR_FORWARD
#define FFT_STAGES XF_FFT_STAGES(FFT_SIZE)
#define FFT_CONFIG XF_FFT_CONFIG(FFT_SIZE, DATA_WIDTH, SCALE_MODE, TWIDDLE_TABLE, OUTPUT_ORDER, INPUT_ORDER, DATA_TYPE, TWIDDLE_TYPE, SCALE_TYPE, FFT_TYPE, FFT_DIR, FFT_STAGES)

// –°—Ç—Ä—É–∫—Ç—É—Ä–∞ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –∫–æ—ç—Ñ—Ñ–∏—Ü–∏–µ–Ω—Ç–æ–≤
typedef xf::dsp::fft::fft_config<FFT_CONFIG> fft_config_t;

// –§—É–Ω–∫—Ü–∏—è FFT
void fft_top(
    hls::stream<ap_uint<DATA_WIDTH*2>> &inStream,
    hls::stream<ap_uint<DATA_WIDTH*2>> &outStream,
    const ap_uint<DATA_WIDTH*2> twiddleTable[FFT_SIZE/2],
    const ap_uint<DATA_WIDTH*2> bitReverseTable[FFT_SIZE]
) {
    #pragma HLS INTERFACE axis port=inStream
    #pragma HLS INTERFACE axis port=outStream
    #pragma HLS INTERFACE s_axilite port=return
    #pragma HLS INTERFACE s_axilite port=twiddleTable
    #pragma HLS INTERFACE s_axilite port=bitReverseTable
    #pragma HLS DATA_PACK variable=twiddleTable
    #pragma HLS DATA_PACK variable=bitReverseTable
    #pragma HLS DATAFLOW

    // –ë—É—Ñ–µ—Ä–∏–∑–∞—Ü–∏—è –≤—Ö–æ–¥–Ω–æ–≥–æ –ø–æ—Ç–æ–∫–∞
    ap_uint<DATA_WIDTH*2> inBuf[FFT_SIZE];
    #pragma HLS ARRAY_PARTITION variable=inBuf complete dim=1
    for(int i = 0; i < FFT_SIZE; i++) {
        #pragma HLS PIPELINE II=1
        inBuf[i] = inStream.read();
    }

    // –ü—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –≤ –∫–æ–º–ø–ª–µ–∫—Å–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç
    xf::complex<ap_fixed<DATA_WIDTH, 1, AP_TRN, AP_SAT>> inData[FFT_SIZE];
    #pragma HLS ARRAY_PARTITION variable=inData complete dim=1
    for(int i = 0; i < FFT_SIZE; i++) {
        #pragma HLS UNROLL
        inData[i].real(inBuf[i](DATA_WIDTH-1, 0));
        inData[i].imag(inBuf[i](2*DATA_WIDTH-1, DATA_WIDTH));
    }

    // –í—ã–ø–æ–ª–Ω–µ–Ω–∏–µ FFT
    xf::complex<ap_fixed<DATA_WIDTH, 1, AP_TRN, AP_SAT>> outData[FFT_SIZE];
    #pragma HLS ARRAY_PARTITION variable=outData complete dim=1
    xf::dsp::fft::fft<FFT_CONFIG>(inData, outData, twiddleTable, bitReverseTable);

    // –ü—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ –æ–±—Ä–∞—Ç–Ω–æ –≤ —É–ø–∞–∫–æ–≤–∞–Ω–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç
    ap_uint<DATA_WIDTH*2> outBuf[FFT_SIZE];
    #pragma HLS ARRAY_PARTITION variable=outBuf complete dim=1
    for(int i = 0; i < FFT_SIZE; i++) {
        #pragma HLS UNROLL
        outBuf[i](DATA_WIDTH-1, 0) = outData[i].real();
        outBuf[i](2*DATA_WIDTH-1, DATA_WIDTH) = outData[i].imag();
    }

    // –í—ã–≤–æ–¥ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
    for(int i = 0; i < FFT_SIZE; i++) {
        #pragma HLS PIPELINE II=1
        outStream.write(outBuf[i]);
    }
}
```

