5. **–û—á–∏—Å—Ç–∫–∞ —Ä–µ—Å—É—Ä—Å–æ–≤**: –û—Å–≤–æ–±–æ–∂–¥–µ–Ω–∏–µ –≤—ã–¥–µ–ª–µ–Ω–Ω–æ–π –ø–∞–º—è—Ç–∏.

### –ö–æ–¥ –Ω–∞ LLVM-C
```c
#include <llvm-c/Core.h>
#include <llvm-c/ExecutionEngine.h>
#include <llvm-c/Target.h>
#include <llvm-c/Analysis.h>
#include <llvm-c/BitWriter.h>
#include <stdio.h>
#include <stdlib.h>

int main() {
    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è LLVM
    LLVMInitializeAllTargets();
    LLVMInitializeAllTargetInfos();
    LLVMInitializeAllAsmPrinters();
    LLVMInitializeAllAsmParsers();

    // –°–æ–∑–¥–∞–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ –∏ –º–æ–¥—É–ª—è
    LLVMContextRef context = LLVMGetGlobalContext();
    LLVMModuleRef module = LLVMModuleCreateWithNameInContext("fib_module", context);

    // –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Ç–∏–ø–∞ —Ñ—É–Ω–∫—Ü–∏–∏ fib(n): i32 -> i32
    LLVMTypeRef int32_type = LLVMInt32TypeInContext(context);
    LLVMTypeRef function_type = LLVMFunctionType(int32_type, &int32_type, 1, 0);
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", function_type);

    // –°–æ–∑–¥–∞–Ω–∏–µ –±–ª–æ–∫–æ–≤ –∫–æ–¥–∞
    LLVMBasicBlockRef entry_block = LLVMAppendBasicBlockInContext(context, fib_func, "entry");
    LLVMBasicBlockRef base_case_block = LLVMAppendBasicBlockInContext(context, fib_func, "base_case");
    LLVMBasicBlockRef recursive_block = LLVMAppendBasicBlockInContext(context, fib_func, "recursive");

    // –í—Ö–æ–¥–Ω–æ–π –±–ª–æ–∫
    LLVMBuilderRef builder = LLVMCreateBuilderInContext(context);
    LLVMPositionBuilderAtEnd(builder, entry_block);
    LLVMValueRef n = LLVMGetParam(fib_func, 0);
    LLVMValueRef cond = LLVMBuildICmp(builder, LLVMIntSLE, n, LLVMConstInt(int32_type, 1, 0), "cond");
    LLVMBuildCondBr(builder, cond, base_case_block, recursive_block);

    // –ë–ª–æ–∫ –±–∞–∑–æ–≤–æ–≥–æ —Å–ª—É—á–∞—è
    LLVMPositionBuilderAtEnd(builder, base_case_block);
    LLVMValueRef ret_val = LLVMBuildPhi(builder, int32_type, "ret_val");
    LLVMAddIncoming(ret_val, &n, &base_case_block, 1);
    LLVMBuildRet(builder, ret_val);

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π –±–ª–æ–∫
    LLVMPositionBuilderAtEnd(builder, recursive_block);
    LLVMValueRef n_minus_1 = LLVMBuildSub(builder, n, LLVMConstInt(int32_type, 1, 0), "n-1");
    LLVMValueRef fib_n_minus_1 = LLVMBuildCall(builder, fib_func, &n_minus_1, 1, "fib(n-1)");
