```java
import java.util.Scanner;

class Fraction {
    private int numerator;
    private int denominator;

    public Fraction(int numerator, int denominator) {
        if (denominator == 0) {
            throw new IllegalArgumentException("–ó–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å —Ä–∞–≤–µ–Ω –Ω—É–ª—é");
        }
        this.numerator = numerator;
        this.denominator = denominator;
        simplify();
    }

    private void simplify() {
        int gcd = gcd(Math.abs(numerator), Math.abs(denominator));
        numerator /= gcd;
        denominator /= gcd;

        // –£–±–µ–¥–∏–º—Å—è, —á—Ç–æ –∑–Ω–∞–∫ —Ö—Ä–∞–Ω–∏—Ç—Å—è —Ç–æ–ª—å–∫–æ –≤ —á–∏—Å–ª–∏—Ç–µ–ª–µ
        if (denominator < 0) {
            numerator = -numerator;
            denominator = -denominator;
        }
    }

    private int gcd(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }

    public Fraction add(Fraction other) {
        int newNumerator = numerator * other.denominator + other.numerator * denominator;
        int newDenominator = denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    public Fraction subtract(Fraction other) {
        int newNumerator = numerator * other.denominator - other.numerator * denominator;
        int newDenominator = denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    public Fraction multiply(Fraction other) {
        int newNumerator = numerator * other.numerator;
        int newDenominator = denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    public Fraction divide(Fraction other) {
        if (other.numerator == 0) {
            throw new ArithmeticException("–î–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å –Ω–µ–≤–æ–∑–º–æ–∂–Ω–æ");
        }
        int newNumerator = numerator * other.denominator;
        int newDenominator = denominator * other.numerator;
        return new Fraction(newNumerator, newDenominator);
    }

    @Override
    public String toString() {
        if (denominator == 1) {
            return Integer.toString(numerator);
        }
        return numerator + "/" + denominator;
    }
}

public class FractionCalculator {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("–í–≤–µ–¥–∏—Ç–µ –≤—ã—Ä–∞–∂–µ–Ω–∏–µ (–Ω–∞–ø—Ä–∏–º–µ—Ä: 1/2 + 3/4):");

        try {
            String input = scanner.nextLine().trim();
            String[] parts = input.split("\\s+");

            if (parts.length != 3) {
                System.out.println("–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –≤—ã—Ä–∞–∂–µ–Ω–∏—è");
                return;
            }

            String[] firstFraction = parts[0].split("/");
            String[] secondFraction = parts[2].split("/");

            int num1 = Integer.parseInt(firstFraction[0]);
            int den1 = firstFraction.length > 1 ? Integer.parseInt(firstFraction[1]) : 1;
            int num2 = Integer.parseInt(secondFraction[0]);
            int den2 = secondFraction.length > 1 ? Integer.parseInt(secondFraction[1]) : 1;

            Fraction f1 = new Fraction(num1, den1);
            Fraction f2 = new Fraction(num2, den2);
            Fraction result;

            switch (parts[1]) {
                case "+":
                    result = f1.add(f2);
                    break;
                case "-":
                    result = f1.subtract(f2);
                    break;
                case "*":
                    result = f1.multiply(f2);
                    break;
                case "/":
                    result = f1.divide(f2);
                    break;
                default:
                    System.out.println("–ù–µ–ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º–∞—è –æ–ø–µ—Ä–∞—Ü–∏—è");
                    return;
            }

            System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç: " + result);
