–í–æ—Ç –ø—Ä–æ—Å—Ç–æ–π –ø—Ä–∏–º–µ—Ä –∫–æ–¥–∞ –Ω–∞ C#, –∫–æ—Ç–æ—Ä—ã–π –≤—ã—á–∏—Å–ª—è–µ—Ç –∑–Ω–∞—á–µ–Ω–∏–µ —á–∏—Å–ª–∞ –ü–∏ —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º —Ñ–æ—Ä–º—É–ª—ã –ß—É–¥–∞-–°–∞–≤–∞—Ä—ã. –≠—Ç–æ—Ç –º–µ—Ç–æ–¥ –ø–æ–∑–≤–æ–ª—è–µ—Ç –ø–æ–ª—É—á–∞—Ç—å –¥–æ–≤–æ–ª—å–Ω–æ —Ç–æ—á–Ω—ã–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã –∑–∞ –Ω–µ–±–æ–ª—å—à–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∏—Ç–µ—Ä–∞—Ü–∏–π:

```csharp
using System;

class Program
{
    static void Main()
    {
        int iterations = 10; // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∏—Ç–µ—Ä–∞—Ü–∏–π –¥–ª—è –ø–æ–≤—ã—à–µ–Ω–∏—è —Ç–æ—á–Ω–æ—Å—Ç–∏
        decimal pi = CalculatePi(iterations);

        Console.WriteLine($"–í—ã—á–∏—Å–ª–µ–Ω–Ω–æ–µ –∑–Ω–∞—á–µ–Ω–∏–µ œÄ: {pi:F20}");
        Console.WriteLine($"–ó–Ω–∞—á–µ–Ω–∏–µ œÄ –∏–∑ Math.PI: {Math.PI:F20}");
    }

    static decimal CalculatePi(int iterations)
    {
        decimal pi = 0;
        decimal factor = 1;
        decimal denominator = 1;
        decimal term = 1;
        decimal epsilon = 1e-30M; // –ö—Ä–∏—Ç–µ—Ä–∏–π –æ—Å—Ç–∞–Ω–æ–≤–∫–∏ –¥–ª—è –æ—á–µ–Ω—å –º–∞–ª—ã—Ö –∑–Ω–∞—á–µ–Ω–∏–π

        for (int k = 0; k < iterations; k++)
        {
            decimal currentTerm = factor * (4m / (8 * k + 1) - 2m / (8 * k + 4) - 1m / (8 * k + 5) - 1m / (8 * k + 6));
            pi += currentTerm / (16m)^(k);

            // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –Ω–µ —Å—Ç–∞–ª–æ –ª–∏ —Å–ª–∞–≥–∞–µ–º–æ–µ —Å–ª–∏—à–∫–æ–º –º–∞–ª–µ–Ω—å–∫–∏–º
            if (Math.Abs(currentTerm) < epsilon)
                break;
        }

        return pi;
    }
}
```

