    result = std::regex_replace(result, lineRegex, "\n");

    return result;
}

// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–æ–±—ã—Ç–∏—è –≤—Å—Ç–∞–≤–∫–∏ —Ç–µ–∫—Å—Ç–∞
void onInsertText(ScintillaEditView* pEditView, const char* text, int length) {
    if (pEditView && text && length > 0) {
        std::string cleanedText = cleanText(std::string(text, length));
        if (cleanedText != text) {
            pEditView->insertText(pEditView->getCurrentPos(), cleanedText.c_str(), cleanedText.length());
        }
    }
}

