### **–ü—Ä–∏–º–µ—Ä –∫–æ–¥–∞ –Ω–∞ C —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º LLVM-C**

```c
#include <llvm-c/Core.h>
#include <llvm-c/ExecutionEngine.h>
#include <llvm-c/Target.h>
#include <llvm-c/Analysis.h>
#include <stdio.h>
#include <stdlib.h>

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—Å–≤–æ–±–æ–∂–¥–µ–Ω–∏—è –ø–∞–º—è—Ç–∏
void cleanup(LLVMContextRef context, LLVMModuleRef module, LLVMExecutionEngineRef engine) {
    if (engine) LLVMDisposeExecutionEngine(engine);
    if (module) LLVMDisposeModule(module);
    if (context) LLVMDisposeContext(context);
}

int main() {
    // –®–∞–≥ 1: –°–æ–∑–¥–∞–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ –∏ –º–æ–¥—É–ª—è
    LLVMContextRef context = LLVMContextCreate();
    LLVMModuleRef module = LLVMModuleCreateWithNameInContext("fib_module", context);

    // –®–∞–≥ 2: –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Ç–∏–ø–∞ —Ñ—É–Ω–∫—Ü–∏–∏ fib(i64) -> i64
    LLVMPTypeRef i64_type = LLVMInt64TypeInContext(context);
    LLVMPTypeRef function_type = LLVMFunctionType(i64_type, &i64_type, 1, 0);
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", function_type);
    LLVMAddFunctionAttr(fib_func, LLVMNoUnwindAttribute); // –ê—Ç—Ä–∏–±—É—Ç NoUnwind

    // –®–∞–≥ 3: –°–æ–∑–¥–∞–Ω–∏–µ –±–ª–æ–∫–æ–≤ Basic Block
    LLVMBasicBlockRef entry_block = LLVMAppendBasicBlock(fib_func, "entry");
    LLVMBuilderRef builder = LLVMCreateBuilderInContext(context);
    LLVMPositionBuilderAtEnd(builder, entry_block);

    // –®–∞–≥ 4: –ü–æ–ª—É—á–µ–Ω–∏–µ –∞—Ä–≥—É–º–µ–Ω—Ç–∞ —Ñ—É–Ω–∫—Ü–∏–∏
    LLVMValueRef n = LLVMGetParam(fib_func, 0);
    LLVMValueRef zero = LLVMConstInt(i64_type, 0, 0);
    LLVMValueRef one = LLVMConstInt(i64_type, 1, 0);
    LLVMValueRef two = LLVMConstInt(i64_type, 2, 0);

    // –ë–∞–∑–æ–≤—ã–π —Å–ª—É—á–∞–π: n == 0
    LLVMValueRef cond1 = LLVMBuildICmp(builder, LLVMIntEQ, n, zero, "cond1");
    LLVMValueRef ret1 = LLVMBuildRet(builder, zero);
    LLVMValueRef cond_br1 = LLVMBuildCondBr(builder, cond1, entry_block, entry_block);
    LLVMPositionBuilderAtEnd(builder, entry_block);

    // –ë–∞–∑–æ–≤—ã–π —Å–ª—É—á–∞–π: n == 1
    LLVMValueRef cond2 = LLVMBuildICmp(builder, LLVMIntEQ, n, one, "cond2");
    LLVMValueRef ret2 = LLVMBuildRet(builder, one);
    LLVMValueRef cond_br2 = LLVMBuildCondBr(builder, cond2, entry_block, entry_block);
    LLVMPositionBuilderAtEnd(builder, entry_block);

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π —Å–ª—É—á–∞–π: fib(n-1) + fib(n-2)
    LLVMValueRef n_minus_1 = LLVMBuildSub(builder, n, one, "n-1");
    LLVMValueRef n_minus_2 = LLVMBuildSub(builder, n, two, "n-2");
    LLVMValueRef call1 = LLVMBuildCall(builder, fib_func, &n_minus_1, 1, "call1");
    LLVMValueRef call2 = LLVMBuildCall(builder, fib_func, &n_minus_2, 1, "call2");
    LLVMValueRef sum = LLVMBuildAdd(builder, call1, call2, "sum");
    LLVMValueRef ret3 = LLVMBuildRet(builder, sum);

    // –®–∞–≥ 5: –ö–æ–º–ø–∏–ª—è—Ü–∏—è –∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–µ
    LLVMExecutionEngineRef engine;
    char *error = NULL;
    if (LLVMCreateJITCompilerForModule(&engine, module, 20, &error) != 0) {
        fprintf(stderr, "–û—à–∏–±–∫–∞ –∫–æ–º–ø–∏–ª—è—Ü–∏–∏: %s\n", error);
        free(error);
        cleanup(context, module, NULL);
        return 1;
    }

    // –ü–æ–ª—É—á–µ–Ω–∏–µ —É–∫–∞–∑–∞—Ç–µ–ª—è –Ω–∞ —Ñ—É–Ω–∫—Ü–∏—é
    void *fib_ptr;
    if (LLVMGetPointerToGlobal(engine, fib_func, &fib_ptr) != 0) {
        fprintf(stderr, "–û—à–∏–±–∫–∞ –ø–æ–ª—É—á–µ–Ω–∏—è —É–∫–∞–∑–∞—Ç–µ–ª—è –Ω–∞ —Ñ—É–Ω–∫—Ü–∏—é\n");
        cleanup(context, module, engine);
        return 1;
    }

    // –í—ã–∑–æ–≤ —Ñ—É–Ω–∫—Ü–∏–∏
    typedef int64_t (*FibFunc)(int64_t);
    FibFunc fib = (FibFunc)fib_ptr;
    printf("fib(10) = %ld\n", fib(10)); // –ü—Ä–∏–º–µ—Ä –≤—ã–∑–æ–≤–∞ —Ñ—É–Ω–∫—Ü–∏–∏

    // –®–∞–≥ 6: –û—Å–≤–æ–±–æ–∂–¥–µ–Ω–∏–µ —Ä–µ—Å—É—Ä—Å–æ–≤
    cleanup(context, module, engine);
    return 0;
}
```

