std::wstring removeWhitespace(const std::wstring& text) {
    std::wstring result;
    for (wchar_t c : text) {
        if (!std::isspace(c)) { // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —è–≤–ª—è–µ—Ç—Å—è –ª–∏ —Å–∏–º–≤–æ–ª –ø—Ä–æ–±–µ–ª–æ–º
            result += c;
