}

// –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–æ–±—ã—Ç–∏—è –≤—Å—Ç–∞–≤–∫–∏ —Ç–µ–∫—Å—Ç–∞
void onInsertText(HWND hWnd, UINT wParam, WPARAM lParam) {
    HWND hwndEdit = GetDlgItem(hWnd, ID_EDIT);
