    // –£–¥–∞–ª—è–µ–º –ø—Ä–æ–±–µ–ª—ã
    std::wstring cleanedText = removeWhitespace(buffer);

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –æ—á–∏—â–µ–Ω–Ω—ã–π —Ç–µ–∫—Å—Ç
    SendMessage(hwndEdit, EM_REPLACESEL, FALSE, reinterpret_cast<LPARAM>(cleanedText.c_str()));
}

// –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è –ø–ª–∞–≥–∏–Ω–∞
