import SwiftUI

struct ContentView: View {
    @State private var firstNumber: Double = 0
    @State private var secondNumber: Double = 0
    @State private var result: String = "0"

    let operations: [(String, (Double, Double) -> Double)] = [
        ("‚ûï", +),
        ("‚ûñ", -),
        ("‚úñÔ∏è", *),
        ("ÔøΩ", /)
    ]

    var body: some View {
        VStack(spacing: 20) {
            Text("–ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä")
                .font(.largeTitle)

            TextField("–í–≤–µ–¥–∏—Ç–µ —á–∏—Å–ª–æ 1", value: $firstNumber, formatter: NumberFormatter())
                .textFieldStyle(.roundedBorder)

            TextField("–í–≤–µ–¥–∏—Ç–µ —á–∏—Å–ª–æ 2", value: $secondNumber, formatter: NumberFormatter())
                .textFieldStyle(.roundedBorder)

            Picker(selection: $operations.indices.wrapped, label: Text("–í—ã–±–µ—Ä–∏—Ç–µ –æ–ø–µ—Ä–∞—Ü–∏—é")) {
                ForEach(operations.indices, id: \.self) { index in
                    Text(operations[index].0).tag(index)
