</head>
<body>
    <canvas id="myCanvas" width="400" height="200"></canvas>

    <script>
        // –ü–æ–ª—É—á–∞–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç —Ä–∏—Å–æ–≤–∞–Ω–∏—è
        const canvas = document.getElementById('myCanvas');
        const ctx = canvas.getContext('2d');

        // –†–∏—Å—É–µ–º –Ω–∞—á–∞–ª—å–Ω–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ (–∫—Ä–∞—Å–Ω—ã–π –ø—Ä—è–º–æ—É–≥–æ–ª—å–Ω–∏–∫)
        ctx.fillStyle = 'red';
