            pixels[i + 1] = 0;   // –ó–µ–ª–µ–Ω—ã–π (G)
            pixels[i + 2] = 255; // –°–∏–Ω–∏–π (B)
            pixels[i + 3] = 255; // –ê–ª—å—Ñ–∞-–∫–∞–Ω–∞–ª (–ø—Ä–æ–∑—Ä–∞—á–Ω–æ—Å—Ç—å)
        }

