```java
import java.util.Scanner;

public class FractionCalculator {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        FractionCalculator calculator = new FractionCalculator();
        calculator.run();
        scanner.close();
    }

    public void run() {
        Scanner scanner = new Scanner(System.in);
        System.out.println("Fraction Calculator");
        System.out.print("Enter first fraction (numerator/denominator): ");
        Fraction a = readFraction(scanner);

        System.out.print("Enter operation (+, -, *, /): ");
        char op = scanner.next().charAt(0);

        System.out.print("Enter second fraction: ");
        Fraction b = readFraction(scanner);

        try {
            Fraction result = performOperation(a, b, op);
            System.out.println("Result: " + result);
        } catch (IllegalArgumentException e) {
            System.out.println("Error: " + e.getMessage());
        }
        scanner.close();
    }

    private Fraction readFraction(Scanner scanner) {
        System.out.print("Enter fraction (e.g., 1/2 or -3/4): ");
        String input = scanner.nextLine();
        String[] parts = input.split("/");

        if (parts.length != 2) {
            throw new IllegalArgumentException("Invalid format. Please enter in the form 'numerator/denominator'");
        }

        int numerator = Integer.parseInt(parts[0]);
        int denominator = Integer.parseInt(parts[1]);

        return new Fraction(numerator, denominator);
    }

    private Fraction performOperation(Fraction a, Fraction b, char op) throws IllegalArgumentException {
        switch (op) {
            case '+':
                return a.add(b);
            case '-':
                return a.subtract(b);
            case '*':
                return a.multiply(b);
            case '/':
                return a.divide(b);
            default:
                throw new IllegalArgumentException("Unsupported operation: " + op);
        }
    }
}

class Fraction {
    private int numerator;
    private int denominator;

    public Fraction(int numerator, int denominator) {
        if (denominator == 0) {
            throw new IllegalArgumentException("Denominator cannot be zero.");
        }
        this.numerator = numerator;
        this.denominator = denominator;
        simplify();
    }

    private void simplify() {
        int gcd = gcd(Math.abs(numerator), Math.abs(denominator));
        numerator /= gcd;
        denominator /= gcd;

        if (denominator < 0) {
            numerator *= -1;
            denominator *= -1;
        }
    }

    private int gcd(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }

    public Fraction add(Fraction other) {
        int newNumerator = this.numerator * other.denominator + other.numerator * this.denominator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    public Fraction subtract(Fraction other) {
        int newNumerator = this.numerator * other.denominator - other.numerator * this.denominator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    public Fraction multiply(Fraction other) {
        int newNumerator = this.numerator * other.numerator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    public Fraction divide(Fraction other) {
        if (other.isZero()) {
            throw new IllegalArgumentException("Cannot divide by zero.");
        }
        int newNumerator = this.numerator * other.denominator;
        int newDenominator = this.denominator * other.numerator;
        return new Fraction(newNumerator, newDenominator);
    }

    public boolean isZero() {
        return numerator == 0;
    }

    @Override
    public String toString() {
        if (denominator == 1) {
            return Integer.toString(numerator);
        } else {
            return numerator + "/" + denominator;
        }
    }
}
