```csharp
using System;
using System.Numerics;

class Program
{
    static void Main()
    {
        Console.WriteLine("–í–≤–µ–¥–∏—Ç–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –¥–µ—Å—è—Ç–∏—á–Ω—ã—Ö –∑–Ω–∞–∫–æ–≤ –¥–ª—è –≤—ã—á–∏—Å–ª–µ–Ω–∏—è —á–∏—Å–ª–∞ œÄ:");
        int digits = int.Parse(Console.ReadLine());

        // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–Ω–∞–∫–æ–≤ –≤ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∏—Ç–µ—Ä–∞—Ü–∏–π
        int iterations = (int)Math.Pow(10, digits);

        // –í—ã—á–∏—Å–ª—è–µ–º —Ä—è–¥ –¢–µ–π–ª–æ—Ä–∞ –¥–ª—è œÄ/4
        BigInteger numerator = 1;
        BigInteger denominator = 1;
        BigInteger sign = 1;
        BigInteger sum = 0;

        for (int i = 0; i < iterations; i++)
        {
            sum += sign * numerator / denominator;
            numerator += 2;
            denominator += 2;
            sign *= -1;
        }

        // –£–º–Ω–æ–∂–∞–µ–º –Ω–∞ 4 –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è œÄ
        BigInteger piApproximation = sum * 4;

        // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º —á–∏—Å–ª–æ –≤ —Å—Ç—Ä–æ–∫—É –∏ –∏–∑–≤–ª–µ–∫–∞–µ–º –¥–µ—Å—è—Ç–∏—á–Ω—ã–µ –∑–Ω–∞–∫–∏
        string piString = piApproximation.ToString();
        if (piString.Length > 1)
        {
            Console.WriteLine($"–ß–∏—Å–ª–æ œÄ ‚âà {piString[0]}.{piString.Substring(1)}");
        }
        else
        {
            Console.WriteLine("–û—à–∏–±–∫–∞: –Ω–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Ü–∏—Ñ—Ä –¥–ª—è –ø—Ä–µ–¥—Å—Ç–∞–≤–ª–µ–Ω–∏—è.");
        }
    }
}
