### 2. **–ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ –¥–æ–±–∞–≤–ª–µ–Ω–∏–µ –∞–±–∑–∞—Ü–µ–≤ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ —Ç–µ–∫—Å—Ç–∞**
–ï—Å–ª–∏ –≤—ã —Ö–æ—Ç–∏—Ç–µ, —á—Ç–æ–±—ã –∫–∞–∂–¥—ã–π –Ω–æ–≤—ã–π –∞–±–∑–∞—Ü –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ –æ—Ç–¥–µ–ª—è–ª—Å—è –æ—Ç –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ, —Å–æ–∑–¥–∞–π—Ç–µ —Å–∫—Ä–∏–ø—Ç, –∫–æ—Ç–æ—Ä—ã–π –æ—Ç—Å–ª–µ–∂–∏–≤–∞–µ—Ç –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ç–µ–∫—Å—Ç–∞ –∏ –ø—Ä–∏–º–µ–Ω—è–µ—Ç `<br>` –º–µ–∂–¥—É –Ω–∏–º–∏:
```csharp
using UnityEngine;
using TMPro;

public class AutoParagraph : MonoBehaviour
{
    public TextMeshProUGUI pageTextUI;

    private string lastParagraph = "";

    void Update()
    {
        if (Input.GetKeyDown(KeyCode.Return))
        {
            // –î–æ–±–∞–≤–ª—è–µ–º –∞–±–∑–∞—Ü –ø–æ—Å–ª–µ –Ω–∞–∂–∞—Ç–∏—è Enter
            pageTextUI.text += "<br>";
        }
    }

    // –ê–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–∞: –æ–±—Ä–∞–±–æ—Ç–∞—Ç—å –∏–∑–º–µ–Ω–µ–Ω–∏–µ —Ç–µ–∫—Å—Ç–∞ —á–µ—Ä–µ–∑ —Å–æ–±—ã—Ç–∏–µ
    public void OnTextChanged(string newText)
    {
        // –†–∞–∑–¥–µ–ª—è–µ–º —Ç–µ–∫—Å—Ç –ø–æ —Å—Ç—Ä–æ–∫–∞–º –∏ –¥–æ–±–∞–≤–ª—è–µ–º <br>
        string[] paragraphs = newText.Split('\n');
        string formattedText = "";
        foreach (var para in paragraphs)
        {
            formattedText += $"{para}<br>";
        }
        pageTextUI.text = formattedText.TrimEnd('<'); // –£–¥–∞–ª—è–µ–º –ª–∏—à–Ω–∏–π <br> –≤ –∫–æ–Ω—Ü–µ
    }
}
```

**–ö–∞–∫ –ø–æ–¥–∫–ª—é—á–∏—Ç—å:**
- –î–æ–±–∞–≤—å—Ç–µ —Å–∫—Ä–∏–ø—Ç –∫ GameObject —Å `TextMeshProUGUI`.
- –í—Ä—É—á–Ω—É—é –≤—ã–∑—ã–≤–∞–π—Ç–µ `OnTextChanged()` –ø—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏ (–Ω–∞–ø—Ä–∏–º–µ—Ä, –ø—Ä–∏ –Ω–∞–∂–∞—Ç–∏–∏ –∫–Ω–æ–ø–∫–∏).

