```c
#include "os.h"

Task tasks[MAX_TASKS];
uint32_t current_task_index = 0;
uint32_t tick_count = 0;

void os_init(void) {
    for (int i = 0; i < MAX_TASKS; i++) {
        tasks[i].state = TERMINATED;
    }
}

void os_start(void) {
    // –ó–∞–ø—É—Å–∫ –ø–µ—Ä–≤–æ–π –∑–∞–¥–∞—á–∏
    tasks[current_task_index].task_func = &task1;
    tasks[current_task_index].priority = 1;
    tasks[current_task_index].state = RUNNING;
    current_task_index = 0;
    os_schedule();
}

void os_schedule(void) {
    // –í—ã–±–∏—Ä–∞–µ–º –∑–∞–¥–∞—á—É —Å –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–π –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç–æ–º
    int highest_priority = -1;
    int selected_task = -1;

    for (int i = 0; i < MAX_TASKS; i++) {
        if (tasks[i].state == RUNNING && tasks[i].priority > highest_priority) {
            highest_priority = tasks[i].priority;
            selected_task = i;
        }
    }

    if (selected_task != -1) {
        current_task_index = selected_task;
        // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ (—É–ø—Ä–æ—â—ë–Ω–Ω–æ–µ)
        asm("mov r0, %0" : "+r"(tasks[selected_task].stack));
        asm("bx lr");
    }
}

void os_yield(void) {
    os_schedule();
}

void os_sleep(uint32_t ms) {
    tick_count += ms / 1000; // –ü—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏–µ –º—Å –≤ —Ç–∏–∫–∏ (1 —Ç–∏–∫ = 1 –º—Å)
    while (tick_count <= ms / 1000) {
        os_yield();
    }
}
```

