    <canvas id="myCanvas" width="300" height="200"></canvas>
    <button onclick="drawRandomPattern()">–°–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞—Ç—å —Å–ª—É—á–∞–π–Ω—ã–π —É–∑–æ—Ä</button>
    <button onclick="analyzePixels()">–ê–Ω–∞–ª–∏–∑ –ø–∏–∫—Å–µ–ª–µ–π</button>
    <div id="output"></div>

    <script>
        const canvas = document.getElementById('myCanvas');
        const ctx = canvas.getContext('2d');

        // –†–∏—Å—É–µ–º —Ñ–æ–Ω (–±–µ–ª—ã–π)
        ctx.fillStyle = 'white';
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ —Å–ª—É—á–∞–π–Ω—ã—Ö —É–∑–æ—Ä–æ–≤
        function drawRandomPattern() {
