# –ì–µ–Ω–µ—Ä–∞—Ü–∏—è —Ñ—É–Ω–∫—Ü–∏–∏ Fib —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º LLVM-C API

## –ü—Ä–æ—Ü–µ—Å—Å —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–∏
1. –°–æ–∑–¥–∞–µ–º –º–æ–¥—É–ª—å LLVM
2. –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–∏–ø –¥–∞–Ω–Ω—ã—Ö (unsigned int)
3. –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é fib —Å –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–º n
4. –î–æ–±–∞–≤–ª—è–µ–º –±–∞–∑–æ–≤—ã–µ —Å–ª—É—á–∞–∏ (n=0 –∏ n=1)
5. –†–µ–∞–ª–∏–∑—É–µ–º —Ä–µ–∫—É—Ä—Å–∏–≤–Ω—É—é –ª–æ–≥–∏–∫—É –≤—ã—á–∏—Å–ª–µ–Ω–∏—è –§–∏–±–æ–Ω–∞—á—á–∏
6. –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º IR-–∫–æ–¥
7. –ö–æ–º–ø–∏–ª–∏—Ä—É–µ–º –∏ –≤—ã–≤–æ–¥–∏–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç

## –ö–æ–¥ —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏ —Å —Ö–æ—Ä–æ—à–µ–π –ø—Ä–∞–∫—Ç–∏–∫–æ–π

```c
#include <stdio.h>
#include <llvm-c/Core.h>
#include <llvm-c/ExecutionEngine.h>
#include <llvm-c/Target.h>
#include <llvm-c/Transforms/Instrumentation.h>

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è –º–æ–¥—É–ª—è –∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ñ—É–Ω–∫—Ü–∏–∏ fib
LLVMValueRef create_fib_function(LLVMModuleRef module) {
    // –û–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Ç–∏–ø–∞ unsigned int
    LLVMTypeRef int_type = LLVMIntTypeGetPrimitiveType(LLVMGetTypeForSize(32));

    // –°–æ–∑–¥–∞–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏ fib(n: int) -> int
    LLVMFunctionTypeRef fib_func_ty =
        LLVMFunctionType(LLVMGetTypeForSize(32), int_type, 1, false);

    LLVMValueRef fib_func = LLVMFunctionCreateNamed(module, "fib", fib_func_ty);

    // –ë–∞–∑–æ–≤—ã–µ —Å–ª—É—á–∞–∏
    LLVMBasicBlockRef entry_bb = LLVMAppendBasicBlock(fib_func, "entry");
    LLVMBuilderRef builder = LLVMCreateBuilder();

    // –ü–∞—Ä–∞–º–µ—Ç—Ä—ã —Ñ—É–Ω–∫—Ü–∏–∏
    LLVMValueRef n_arg = LLVMGetParam(fib_func, 0);

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –±–∞–∑–æ–≤–æ–≥–æ —Å–ª—É—á–∞—è n=0
    LLVMValueRef zero = LLVMConstInt(int_type, 0, false);
    LLVMValueRef is_zero = LLVMBuildICmp(builder, LLVMIntEQ, n_arg, zero, "");
    LLVMBranchIf(builder, is_zero, entry_bb, NULL);

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –±–∞–∑–æ–≤–æ–≥–æ —Å–ª—É—á–∞—è n=1
    LLVMValueRef one = LLVMConstInt(int_type, 1, false);
    LLVMValueRef is_one = LLVMBuildICmp(builder, LLVMIntEQ, n_arg, one, "");
    LLVMBranchIf(builder, is_one, entry_bb, NULL);

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω–∞—è –ª–æ–≥–∏–∫–∞
    LLVMValueRef n_minus_1 = LLVMBuildSub(builder, n_arg, one, "");
    LLVMValueRef n_minus_2 = LLVMBuildSub(builder, n_minus_1, one, "");

    LLVMValueRef fib_n_minus_1 = LLVMBuildCall(builder, fib_func, &n_minus_1, 1, "");
    LLVMValueRef fib_n_minus_2 = LLVMBuildCall(builder, fib_func, &n_minus_2, 1, "");

    LLVMValueRef result = LLVMBuildAdd(builder, fib_n_minus_1, fib_n_minus_2, "");

    // –í–æ–∑–≤—Ä–∞—â–µ–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
    LLVMBuildRet(builder, result);

    LLVMDisposeBuilder(builder);
    return fib_func;
}

int main() {
    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è LLVM
    LLVMInitializeNativeTarget();
    LLVMInitializeNativeAsmPrinter();

    // –°–æ–∑–¥–∞–Ω–∏–µ –º–æ–¥—É–ª—è
    LLVMModuleRef module = LLVMModuleCreateWithName("fib_module");

    // –°–æ–∑–¥–∞–Ω–∏–µ —Ñ—É–Ω–∫—Ü–∏–∏ fib
    LLVMValueRef fib_func = create_fib_function(module);

    // –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –∫–æ–¥–∞
    LLVMCodeGenQuality quality = LLVMCodeGenQualityDefault;
    LLVMCodeGenOptLevel opt_level = LLVMCodeGenOptLevelNone;
    LLVMCodeGenPasses passes = LLVMCodeGenPassesNone;
    LLVMRunPasses(module, quality, opt_level, passes);

    // –ö–æ–º–ø–∏–ª—è—Ü–∏—è –≤ –æ–±—ä–µ–∫—Ç–Ω—ã–π —Ñ–∞–π–ª
    LLVMTargetRef target = LLVMGetFirstTarget();
    LLVMTargetMachineRef tm = LLVMTargetCreateTarget(target, "", "", 0);
    LLVMTargetMachineSetOptLevel(tm, opt_level);
    LLVMTargetMachineSetRelocModel(tm, LLVMRelocDefault);
    LLVMTargetMachineSetCodeModel(tm, LLVMCodeModelDefault);

    LLVMCompileModuleToFile(module, tm, "fib.o", 0);

    // –í—ã–≤–æ–¥ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
    printf("–§—É–Ω–∫—Ü–∏—è fib —É—Å–ø–µ—à–Ω–æ —Å–æ–∑–¥–∞–Ω–∞\n");

    // –û—á–∏—Å—Ç–∫–∞ —Ä–µ—Å—É—Ä—Å–æ–≤
    LLVMDisposeModule(module);
    LLVMDisposeTarget(tm);
    LLVMDisposeTarget(target);

    return 0;
}
```

## –ö–ª—é—á–µ–≤—ã–µ –ø—Ä–∏–Ω—Ü–∏–ø—ã —Ö–æ—Ä–æ—à–µ–≥–æ –∫–æ–¥–∞:

1. **–ú–æ–¥—É–ª—å–Ω–æ—Å—Ç—å**: –§—É–Ω–∫—Ü–∏–∏ —Ä–∞–∑–¥–µ–ª–µ–Ω—ã –ø–æ –Ω–∞–∑–Ω–∞—á–µ–Ω–∏—é
