                BigInteger.Pow(Q[i], 5),
                BigInteger.Pow(R[i], 5),
                BigInteger.Pow(S[i], 5),
                BigInteger.Pow(T[i], 5),
                BigInteger.Pow(U[i], 5),
                BigInteger.Pow(V[i], 5),
                BigInteger.Pow(W[i], 5),
                BigInteger.Pow(X[i], 5),
                BigInteger.Pow(Y[i], 5),
                BigInteger.Pow(Z[i], 5)
            );

            denominator[i] = BigInteger.Pow(
                BigInteger.Pow(A[i], 6),
                BigInteger.Pow(B[i], 6),
                BigInteger.Pow(C[i], 6),
                BigInteger.Pow(D[i], 6),
                BigInteger.Pow(E[i], 6),
                BigInteger.Pow(F[i], 6),
                BigInteger.Pow(G[i], 6),
                BigInteger.Pow(H[i], 6),
                BigInteger.Pow(I[i], 6),
                BigInteger.Pow(J[i], 6),
                BigInteger.Pow(K[i], 6),
                BigInteger.Pow(L[i], 6),
                BigInteger.Pow(M[i], 6),
                BigInteger.Pow(N[i], 6),
                BigInteger.Pow(O[i], 6),
                BigInteger.Pow(P[i], 6),
                BigInteger.Pow(Q[i], 6),
                BigInteger.Pow(R[i], 6),
                BigInteger.Pow(S[i], 6),
                BigInteger.Pow(T[i], 6),
                BigInteger.Pow(U[i], 6),
                BigInteger.Pow(V[i], 6),
                BigInteger.Pow(W[i], 6),
                BigInteger.Pow(X[i], 6),
                BigInteger.Pow(Y[i], 6),
                BigInteger.Pow(Z[i], 6)
            );
        }

        // –í—ã—á–∏—Å–ª—è–µ–º —Å—É–º–º—É –≤—Å–µ—Ö —Å–ª–∞–≥–∞–µ–º—ã—Ö
        for (int i = 0; i < 10; i++)
        {
            BigInteger fraction = BigInteger.Divide(numerator[i], denominator[i]);
            BigInteger sign = BigInteger.Pow(-1, i);
            BigInteger term = BigInteger.Multiply(fraction, sign);
            BigInteger power = BigInteger.Pow(640320, 3 * i + 3);
            BigInteger finalTerm = BigInteger.Multiply(term, power);
            BigInteger sqrtPi = BigInteger.Pow(4096, 3 * i + 1);
            BigInteger piApprox = BigInteger.Divide(finalTerm, sqrtPi);

            sum[i] = piApprox;
        }

        BigInteger totalSum = BigInteger.Zero;
        foreach (BigInteger term in sum)
        {
            totalSum += term;
        }

        BigInteger sqrtPi = BigInteger.Pow(4096, 3);
        BigInteger piApprox = BigInteger.Divide(totalSum, sqrtPi);

        Console.WriteLine($"–ü—Ä–∏–±–ª–∏–∂–µ–Ω–∏–µ –∫ œÄ: {piApprox}");
        Console.WriteLine($"–¢–æ—á–Ω–æ—Å—Ç—å: –¥–æ 10 –∑–Ω–∞–∫–æ–≤ –ø–æ—Å–ª–µ –∑–∞–ø—è—Ç–æ–π");
    }
}
