                    document.getElementById('progressBar').style.width = percentComplete + '%';
                }
            };

            xhr.onload = function() {
                if (xhr.status === 200) {
                    try {
                        const response = JSON.parse(xhr.responseText);
                        if (response.success) {
                            showResponse(response.message, 'success');
                            // –ú–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å —Å—Å—ã–ª–∫—É –Ω–∞ —Å–∫–∞—á–∏–≤–∞–Ω–∏–µ
                            const downloadLink = document.createElement('a');
                            downloadLink.href = response.url;
                            downloadLink.textContent = 'Download your file';
                            downloadLink.target = '_blank';
                            downloadLink.style.display = 'block';
                            downloadLink.style.marginTop = '10px';
                            document.getElementById('response').appendChild(downloadLink);
                        } else {
                            showResponse(response.error, 'error');
                        }
                    } catch (e) {
                        showResponse('Error parsing server response', 'error');
                    }
                } else {
                    showResponse(`Server responded with status ${xhr.status}`, 'error');
                }
            };

            xhr.onerror = function() {
                showResponse('Network error occurred', 'error');
            };

            xhr.send(formData);
        });

        function showResponse(message, type) {
            const responseDiv = document.getElementById('response');
            responseDiv.className = `response ${type}`;
            responseDiv.textContent = message;
            responseDiv.style.display = 'block';
        }

        function formatBytes(bytes, decimals = 2) {
            if (bytes === 0) return '0 Bytes';
            const k = 1024;
            const dm = decimals < 0 ? 0 : decimals;
            const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
            const i = Math.floor(Math.log(bytes) / Math.log(k));
            return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
        }
    </script>
</body>
