    char[] chars = str.toCharArray();
    int left = 0, right = chars.length - 1;

    while (left < right) {
        // –ú–µ–Ω—è–µ–º —Å–∏–º–≤–æ–ª—ã –º–µ—Å—Ç–∞–º–∏
        char temp = chars[left];
        chars[left] = chars[right];
        chars[right] = temp;
        left++;
        right--;
    }
    return new String(chars);
}
```

–û–±–∞ –≤–∞—Ä–∏–∞–Ω—Ç–∞ —Ä–∞–±–æ—Ç–∞—é—Ç –∑–∞ **O(n)** –≤—Ä–µ–º–µ–Ω–∏ –∏ **O(n)** –ø–∞–º—è—Ç–∏ (–∏–∑-–∑–∞ —Å–æ–∑–¥–∞–Ω–∏—è –Ω–æ–≤–æ–π —Å—Ç—Ä–æ–∫–∏). –ü–µ—Ä–≤—ã–π —Å–ø–æ—Å–æ–± –ø—Ä–µ–¥–ø–æ—á—Ç–∏—Ç–µ–ª—å–Ω–µ–µ –¥–ª—è –±–æ–ª—å—à–∏–Ω—Å—Ç–≤–∞ —Å–ª—É—á–∞–µ–≤ –±–ª–∞–≥–æ–¥–∞—Ä—è –ø—Ä–æ—Å—Ç–æ—Ç–µ –∏ —á–∏—Ç–∞–µ–º–æ—Å—Ç–∏. üöÄ

# –£–ø—Ä–æ—â—ë–Ω–Ω–∞—è RTOS, –ø–æ–¥–æ–±–Ω–∞—è FreeRTOS (—É—á–µ–±–Ω—ã–π –ø—Ä–∏–º–µ—Ä)

–ù–∏–∂–µ –ø—Ä–µ–¥—Å—Ç–∞–≤–ª–µ–Ω –º–∏–Ω–∏–º–∞–ª—å–Ω—ã–π –ø—Ä–∏–º–µ—Ä –æ–ø–µ—Ä–∞—Ü–∏–æ–Ω–Ω–æ–π —Å–∏—Å—Ç–µ–º—ã —Ä–µ–∞–ª—å–Ω–æ–≥–æ –≤—Ä–µ–º–µ–Ω–∏, –≤–¥–æ—Ö–Ω–æ–≤–ª—ë–Ω–Ω–æ–π FreeRTOS. –≠—Ç–æ **—É—á–µ–±–Ω–∞—è —Ä–µ–∞–ª–∏–∑–∞—Ü–∏—è** —Å –∫–æ–æ–ø–µ—Ä–∞—Ç–∏–≤–Ω—ã–º –ø–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ–º (–±–µ–∑ –∞–ø–ø–∞—Ä–∞—Ç–Ω–æ–≥–æ –ø—Ä–µ—Ä—ã–≤–∞–Ω–∏—è —Ç–∞–π–º–µ—Ä–∞).

```c
#include <stdio.h>
#include <stdlib.h>
#include <setjmp.h>
#include <stdbool.h>

// –ú–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –∑–∞–¥–∞—á
#define MAX_TASKS 4

// –°–æ—Å—Ç–æ—è–Ω–∏—è –∑–∞–¥–∞—á–∏
typedef enum {
    TASK_STATE_READY,
    TASK_STATE_RUNNING,
    TASK_STATE_BLOCKED
} TaskState;

// –ö–æ–Ω—Ç–µ–∫—Å—Ç –∑–∞–¥–∞—á–∏ (—É–ø—Ä–æ—â—ë–Ω–Ω—ã–π)
typedef struct {
    jmp_buf context;      // –°–æ—Ö—Ä–∞–Ω—ë–Ω–Ω—ã–µ —Ä–µ–≥–∏—Å—Ç—Ä—ã (–∏–º–∏—Ç–∞—Ü–∏—è)
    void (*task_func)(void*); // –£–∫–∞–∑–∞—Ç–µ–ª—å –Ω–∞ —Ñ—É–Ω–∫—Ü–∏—é –∑–∞–¥–∞—á–∏
    void* arg;            // –ê—Ä–≥—É–º–µ–Ω—Ç –∑–∞–¥–∞—á–∏
    TaskState state;      // –¢–µ–∫—É—â–µ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ
} TaskControlBlock;

// –ì–ª–æ–±–∞–ª—å–Ω—ã–µ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ
TaskControlBlock tasks[MAX_TASKS];
int current_task = -1;
int next_task = 0;

// –ò–º–∏—Ç–∞—Ü–∏—è –∑–∞–¥–µ—Ä–∂–∫–∏ (–≤ —Ä–µ–∞–ª—å–Ω–æ–º –∫–æ–¥–µ –∏—Å–ø–æ–ª—å–∑—É–µ—Ç —Ç–∞–π–º–µ—Ä)
void task_delay(int ms) {
    volatile unsigned long i;
    for (i = 0; i < ms * 1000; i++);
}

// –°–æ–∑–¥–∞–Ω–∏–µ –Ω–æ–≤–æ–π –∑–∞–¥–∞—á–∏
void task_create(void (*func)(void*), void* arg) {
    for (int i = 0; i < MAX_TASKS; i++) {
        if (tasks[i].state == TASK_STATE_READY || 
            tasks[i].state == TASK_STATE_BLOCKED) {

            tasks[i].task_func = func;
            tasks[i].arg = arg;
            tasks[i].state = TASK_STATE_READY;

            // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞ (–ø–µ—Ä–≤—ã–π –≤—Ö–æ–¥ –≤ –∑–∞–¥–∞—á—É)
            if (setjmp(tasks[i].context) == 0) {
                // –ü–µ—Ä–≤—ã–π –≤—ã–∑–æ–≤ - —Å–æ—Ö—Ä–∞–Ω—è–µ–º –Ω–∞—á–∞–ª—å–Ω—ã–π –∫–æ–Ω—Ç–µ–∫—Å—Ç
                continue;
            }
            return;
        }
    }
    printf("ERROR: No free task slots!\n");
}

// –¢–æ—á–∫–∞ –≤—Ö–æ–¥–∞ –¥–ª—è –≤—Å–µ—Ö –∑–∞–¥–∞—á
void task_entry() {
    while (1) {
        // –í—ã–ø–æ–ª–Ω—è–µ–º –æ—Å–Ω–æ–≤–Ω—É—é —Ñ—É–Ω–∫—Ü–∏—é –∑–∞–¥–∞—á–∏
        tasks[current_task].task_func(tasks[current_task].arg);

        // –í–æ–∑–≤—Ä–∞—â–∞–µ–º—Å—è –≤ –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫ –ø–æ—Å–ª–µ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –∏—Ç–µ—Ä–∞—Ü–∏–∏
        tasks[current_task].state = TASK_STATE_READY;
        next_task = (current_task + 1) % MAX_TASKS;
        longjmp(tasks[next_task].context, 1);
    }
}

// –ü–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫ –∑–∞–¥–∞—á (–∫–æ–æ–ø–µ—Ä–∞—Ç–∏–≤–Ω—ã–π)
void scheduler() {
    while (1) {
        for (int i = 0; i < MAX_TASKS; i++) {
            if (tasks[i].state == TASK_STATE_READY) {
                if (current_task != -1) {
                    tasks[current_task].state = TASK_STATE_READY;
                }

                current_task = i;
                tasks[i].state = TASK_STATE_RUNNING;

                // –ü–µ—Ä–µ–∫–ª—é—á–∞–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç
                if (longjmp(tasks[i].context, 1) != 0) {
                    break;
                }
            }
        }
    }
}

// –ü—Ä–∏–º–µ—Ä –∑–∞–¥–∞—á–∏ 1
void task1(void* arg) {
    while (1) {
        printf("Task 1: Hello from task 1!\n");
        task_delay(500);
    }
}

// –ü—Ä–∏–º–µ—Ä –∑–∞–¥–∞—á–∏ 2
void task2(void* arg) {
    while (1) {
        printf("Task 2: Hello from task 2!\n");
        task_delay(1000);
    }
}

int main() {
    printf("Starting simple RTOS...\n");

    // –°–æ–∑–¥–∞—ë–º –∑–∞–¥–∞—á–∏
    task_create(task1, NULL);
    task_create(task2, NULL);

    // –ó–∞–ø—É—Å–∫–∞–µ–º –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫
    scheduler();

    return 0;
}
```

## –ö–ª—é—á–µ–≤—ã–µ –æ—Å–æ–±–µ–Ω–Ω–æ—Å—Ç–∏ —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏:

1. **–ö–æ–æ–ø–µ—Ä–∞—Ç–∏–≤–Ω–æ–µ –ø–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ**: –ó–∞–¥–∞—á–∏ –¥–æ–±—Ä–æ–≤–æ–ª—å–Ω–æ –ø–µ—Ä–µ–¥–∞—é—Ç —É–ø—Ä–∞–≤–ª–µ–Ω–∏–µ (`longjmp`).
2. **–£–ø—Ä–æ—â—ë–Ω–Ω—ã–π –∫–æ–Ω—Ç–µ–∫—Å—Ç**: –ò—Å–ø–æ–ª—å–∑—É–µ—Ç—Å—è `setjmp/longjmp` –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è (–Ω–µ –ø–æ–¥—Ö–æ–¥–∏—Ç –¥–ª—è —Ä–µ–∞–ª—å–Ω–æ–≥–æ –∫–æ–¥–∞!).
3. **–ë–∞–∑–æ–≤—ã–µ –ø—Ä–∏–º–∏—Ç–∏–≤—ã**:
   - –°–æ–∑–¥–∞–Ω–∏–µ –∑–∞–¥–∞—á (`task_create`)
   - –ó–∞–¥–µ—Ä–∂–∫–∞ (`task_delay`)
