            throw new IllegalArgumentException("–ó–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å —Ä–∞–≤–µ–Ω –Ω—É–ª—é");
        }

        // –ù–∞—Ö–æ–¥–∏–º –Ω–∞–∏–±–æ–ª—å—à–∏–π –æ–±—â–∏–π –¥–µ–ª–∏—Ç–µ–ª—å
        int gcd = gcd(Math.abs(numerator), Math.abs(denominator));

        // –ù–æ—Ä–º–∞–ª–∏–∑—É–µ–º –¥—Ä–æ–±—å
        this.numerator = numerator / gcd;
        this.denominator = denominator / gcd;

        // –ï—Å–ª–∏ –∑–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –æ—Ç—Ä–∏—Ü–∞—Ç–µ–ª—å–Ω—ã–π, –ø–µ—Ä–µ–Ω–æ—Å–∏–º –∑–Ω–∞–∫ –≤ —á–∏—Å–ª–∏—Ç–µ–ª—å
        if (this.denominator < 0) {
            this.numerator *= -1;
            this.denominator *= -1;
        }
    }

    // –ê–ª–≥–æ—Ä–∏—Ç–º –ï–≤–∫–ª–∏–¥–∞ –¥–ª—è –Ω–∞—Ö–æ–∂–¥–µ–Ω–∏—è –ù–û–î
    private static int gcd(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }

    // –°–ª–æ–∂–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    public Fraction add(Fraction other) {
        int newNumerator = this.numerator * other.denominator + other.numerator * this.denominator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    // –í—ã—á–∏—Ç–∞–Ω–∏–µ –¥—Ä–æ–±–µ–π
    public Fraction subtract(Fraction other) {
        int newNumerator = this.numerator * other.denominator - other.numerator * this.denominator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    // –£–º–Ω–æ–∂–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    public Fraction multiply(Fraction other) {
        int newNumerator = this.numerator * other.numerator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    // –î–µ–ª–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    public Fraction divide(Fraction other) {
        if (other.numerator == 0) {
            throw new ArithmeticException("–ù–µ–ª—å–∑—è –¥–µ–ª–∏—Ç—å –Ω–∞ –Ω–æ–ª—å");
        }
        int newNumerator = this.numerator * other.denominator;
        int newDenominator = this.denominator * other.numerator;
        return new Fraction(newNumerator, newDenominator);
    }

    // –ü—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏–µ –≤ —Å—Ç—Ä–æ–∫—É
    @Override
    public String toString() {
        if (denominator == 1) {
            return Integer.toString(numerator);
        } else {
            return numerator + "/" + denominator;
        }
    }

    // –°—Ç–∞—Ç–∏—á–µ—Å–∫–∏–π –º–µ—Ç–æ–¥ –¥–ª—è –ø–∞—Ä—Å–∏–Ω–≥–∞ —Å—Ç—Ä–æ–∫–∏ –≤ –¥—Ä–æ–±—å
    public static Fraction parseFraction(String str) throws NumberFormatException {
        str = str.trim();
        if (str.contains("/")) {
            String[] parts = str.split("/");
            if (parts.length != 2) {
                throw new NumberFormatException("–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥—Ä–æ–±–∏: " + str);
            }
            try {
                int num = Integer.parseInt(parts[0]);
                int den = Integer.parseInt(parts[1]);
                return new Fraction(num, den);
            } catch (NumberFormatException e) {
                throw new NumberFormatException("–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–µ —á–∏—Å–ª–æ –≤ –¥—Ä–æ–±–∏: " + str);
            }
        } else {
            try {
                int num = Integer.parseInt(str);
                return new Fraction(num, 1);
            } catch (NumberFormatException e) {
                throw new NumberFormatException("–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–µ —Ü–µ–ª–æ–µ —á–∏—Å–ª–æ: " + str);
            }
        }
    }
}

// –û—Å–Ω–æ–≤–Ω–æ–π –∫–ª–∞—Å—Å –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞
public class FractionCalculator {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("–ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –¥—Ä–æ–±–µ–π");
        System.out.println("–í–≤–µ–¥–∏—Ç–µ –≤—ã—Ä–∞–∂–µ–Ω–∏—è –≤ —Ñ–æ—Ä–º–∞—Ç–µ '–¥—Ä–æ–±—å –æ–ø–µ—Ä–∞—Ü–∏—è –¥—Ä–æ–±—å' (–Ω–∞–ø—Ä–∏–º–µ—Ä, '1/2 + 3/4')");
        System.out.println("–ü–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º—ã–µ –æ–ø–µ—Ä–∞—Ü–∏–∏: +, -, *, /");
        System.out.println("–î–ª—è –≤—ã—Ö–æ–¥–∞ –≤–≤–µ–¥–∏—Ç–µ 'exit'");

        while (true) {
            System.out.print("\n–í–≤–µ–¥–∏—Ç–µ –≤—ã—Ä–∞–∂–µ–Ω–∏–µ: ");
            String input = scanner.nextLine().trim();

            if (input.equalsIgnoreCase("exit")) {
                break;
            }

            try {
                // –†–∞–∑–±–∏–≤–∞–µ–º –≤–≤–æ–¥ –Ω–∞ —á–∞—Å—Ç–∏
                String[] tokens = input.split("\\s+");
                if (tokens.length != 3) {
                    throw new IllegalArgumentException("–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –≤–≤–æ–¥–∞. –û–∂–∏–¥–∞–µ—Ç—Å—è: '–¥—Ä–æ–±—å –æ–ø–µ—Ä–∞—Ü–∏—è –¥—Ä–æ–±—å'");
                }

                String leftStr = tokens[0];
                String op = tokens[1];
                String rightStr = tokens[2];

                // –ü–∞—Ä—Å–∏–º –¥—Ä–æ–±–∏
                Fraction left = Fraction.parseFraction(leftStr);
                Fraction right = Fraction.parseFraction(rightStr);

                // –í—ã–ø–æ–ª–Ω—è–µ–º –æ–ø–µ—Ä–∞—Ü–∏—é
                Fraction result;
                switch (op) {
