                }

                // –í—ã–≤–æ–¥–∏–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
                System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç: " + result);
            } catch (Exception e) {
