### **4. –§–æ—Ä–º—É–ª–∞ –ë—ç–π–ª–∏-–ë–æ—Ä—É—ç–π–Ω–∞-–ü–ª–∞—Ñ—Ñ–∞ (BBP) ‚Äî –≤—ã—á–∏—Å–ª–µ–Ω–∏–µ –æ—Ç–¥–µ–ª—å–Ω—ã—Ö —Ü–∏—Ñ—Ä**
–ü–æ–∑–≤–æ–ª—è–µ—Ç –Ω–∞—Ö–æ–¥–∏—Ç—å **N-—é —à–µ—Å—Ç–Ω–∞–¥—Ü–∞—Ç–µ—Ä–∏—á–Ω—É—é —Ü–∏—Ñ—Ä—É œÄ** –±–µ–∑ –≤—ã—á–∏—Å–ª–µ–Ω–∏—è –ø—Ä–µ–¥—ã–¥—É—â–∏—Ö.

```csharp
using System;

class Program
{
    static void Main()
    {
        int digitPosition = 10; // –ù–æ–º–µ—Ä —Ü–∏—Ñ—Ä—ã (–Ω–∞—á–∏–Ω–∞—è —Å 0)
        string hexDigit = GetHexDigitOfPi(digitPosition);
        Console.WriteLine($"{digitPosition}-—è —à–µ—Å—Ç–Ω–∞–¥—Ü–∞—Ç–µ—Ä–∏—á–Ω–∞—è —Ü–∏—Ñ—Ä–∞ œÄ: {hexDigit}");
    }

    static string GetHexDigitOfPi(int n)
    {
        double sum = 0.0;
        for (int k = 0; k <= n; k++)
        {
            double term = Math.Pow(16, n - k) / 
                          (8 * k + 1) - 
                          4 / (8 * k + 4) - 
                          2 / (8 * k + 5) - 
                          1 / (8 * k + 6);
            sum += term;
        }
        sum = sum % 1; // –ë–µ—Ä–µ–º –¥—Ä–æ–±–Ω—É—é —á–∞—Å—Ç—å
        int digit = (int)(sum * 16);
        return digit.ToString("X"); // –®–µ—Å—Ç–Ω–∞–¥—Ü–∞—Ç–µ—Ä–∏—á–Ω—ã–π –≤—ã–≤–æ–¥
    }
}
```
**–í—ã–≤–æ–¥ (10-—è —Ü–∏—Ñ—Ä–∞):**
```
