def load_json(file_path):
    """–ó–∞–≥—Ä—É–∂–∞–µ—Ç JSON –∏–∑ —Ñ–∞–π–ª–∞."""
    with open(file_path, 'r', encoding='utf-8') as f:
        return json.load(f)

def compare_categories(cat1, cat2):
    """
    –°—Ä–∞–≤–Ω–∏–≤–∞–µ—Ç –¥–≤–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ (–∏ –∏—Ö –ø–æ–¥–∫–∞—Ç–µ–≥–æ—Ä–∏–∏).
    –í–æ–∑–≤—Ä–∞—â–∞–µ—Ç –ø—Ä–æ—Ü–µ–Ω—Ç —Å–æ–≤–ø–∞–¥–µ–Ω–∏—è (0-100).
    """
    score = 0
    max_score = 0

    # 1. –ü–æ–ª–Ω–æ–µ —Å–æ–≤–ø–∞–¥–µ–Ω–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ (–µ—Å–ª–∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∞ –∏–¥–µ–Ω—Ç–∏—á–Ω–∞)
    if cat1 == cat2:
        return 100

    # 2. –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞–ª–∏—á–∏—è –ø–æ–¥–∫–∞—Ç–µ–≥–æ—Ä–∏–π
    subcats1 = set(cat1.get("subcategories", []))
    subcats2 = set(cat2.get("subcategories", []))

    # –í–µ—Å –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ (—á–µ–º –≤—ã—à–µ —É—Ä–æ–≤–µ–Ω—å, —Ç–µ–º –±–æ–ª—å—à–µ –≤–µ—Å)
    weight = 1.0  # –º–æ–∂–Ω–æ –Ω–∞—Å—Ç—Ä–æ–∏—Ç—å (–Ω–∞–ø—Ä–∏–º–µ—Ä, 1.5 –¥–ª—è –≤–µ—Ä—Ö–Ω–µ–≥–æ —É—Ä–æ–≤–Ω—è)

    # a) –°–æ–≤–ø–∞–¥–µ–Ω–∏–µ –ø–æ–¥–∫–∞—Ç–µ–≥–æ—Ä–∏–π (50% –≤–µ—Å–∞)
    common_subcats = subcats1 & subcats2
    if common_subcats:
        score += len(common_subcats) * 50 * weight
        max_score += len(subcats1 | subcats2) * 50 * weight

    # b) –°–æ–≤–ø–∞–¥–µ–Ω–∏–µ —Ç–æ–ª—å–∫–æ –Ω–∞–∑–≤–∞–Ω–∏—è –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ (25% –≤–µ—Å–∞)
    if cat1["name"] == cat2["name"]:
        score += 25 * weight
        max_score += 25 * weight

    # c) –ï—Å–ª–∏ –µ—Å—Ç—å –ø–æ–¥–∫–∞—Ç–µ–≥–æ—Ä–∏–∏, —Ä–µ–∫—É—Ä—Å–∏–≤–Ω–æ –ø—Ä–æ–≤–µ—Ä—è–µ–º –∏—Ö
    for subcat_name in common_subcats:
        if subcat_name in cat1.get("subcategories", {}) and subcat_name in cat2.get("subcategories", {}):
            subcat_score = compare_categories(
                cat1["subcategories"][subcat_name],
                cat2["subcategories"][subcat_name]
            )
            score += subcat_score * 0.5  # –ü–æ–¥–∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –¥–∞—é—Ç 50% –æ—Ç —Å–≤–æ–µ–≥–æ –≤–µ—Å–∞
            max_score += 100 * 0.5

    return min(100, (score / max_score) * 100) if max_score > 0 else 0

def compare_category_structures(struct1, struct2):
    """
    –°—Ä–∞–≤–Ω–∏–≤–∞–µ—Ç —Å—Ç—Ä—É–∫—Ç—É—Ä—ã –∫–∞—Ç–µ–≥–æ—Ä–∏–π (—Å–ª–æ–≤–∞—Ä—å {–∏–º—è_–∫–∞—Ç–µ–≥–æ—Ä–∏–∏: –¥–∞–Ω–Ω—ã–µ}).
    –í–æ–∑–≤—Ä–∞—â–∞–µ—Ç –æ–±—â–∏–π –ø—Ä–æ—Ü–µ–Ω—Ç —Å–æ–≤–ø–∞–¥–µ–Ω–∏—è.
    """
    total_score = 0
    total_possible = 0

    # 1. –û–±—â–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ (–ø–æ–ª–Ω–æ–µ –∏–ª–∏ —á–∞—Å—Ç–∏—á–Ω–æ–µ —Å–æ–≤–ø–∞–¥–µ–Ω–∏–µ)
    common_cats = set(struct1.keys()) & set(struct2.keys())
    for cat_name in common_cats:
        cat_score = compare_categories(struct1[cat_name], struct2[cat_name])
        total_score += cat_score
        total_possible += 100

    # 2. –ö–∞—Ç–µ–≥–æ—Ä–∏–∏ —Ç–æ–ª—å–∫–æ –≤ –ø–µ—Ä–≤–æ–º —Ñ–∞–π–ª–µ (–µ—Å–ª–∏ –æ–Ω–∏ –µ—Å—Ç—å –≤–æ –≤—Ç–æ—Ä–æ–º –∫–∞–∫ –ø–æ–¥–∫–∞—Ç–µ–≥–æ—Ä–∏–∏)
    for cat_name in set(struct1.keys()) - set(struct2.keys()):
        # –ò—â–µ–º, –µ—Å—Ç—å –ª–∏ —ç—Ç–∞ –∫–∞—Ç–µ–≥–æ—Ä–∏—è –∫–∞–∫ –ø–æ–¥–∫–∞—Ç–µ–≥–æ—Ä–∏—è –≤–æ –≤—Ç–æ—Ä–æ–º —Ñ–∞–π–ª–µ
        found = False
        for other_cat in struct2.values():
            if isinstance(other_cat.get("subcategories"), dict) and cat_name in other_cat["subcategories"]:
                subcat_score = compare_categories(
                    struct1[cat_name],
                    other_cat["subcategories"][cat_name]
                )
                total_score += subcat_score * 0.25  # 25% –∑–∞ —Å–æ–≤–ø–∞–¥–µ–Ω–∏–µ –ø–æ–¥–∫–∞—Ç–µ–≥–æ—Ä–∏–∏
                total_possible += 100 * 0.25
                found = True
                break
        if not found:
            total_possible += 100  # –£—á–∏—Ç—ã–≤–∞–µ–º –∫–∞–∫ –≤–æ–∑–º–æ–∂–Ω—ã–π –≤–∫–ª–∞–¥

    # 3. –ö–∞—Ç–µ–≥–æ—Ä–∏–∏ —Ç–æ–ª—å–∫–æ –≤–æ –≤—Ç–æ—Ä–æ–º —Ñ–∞–π–ª–µ (–∞–Ω–∞–ª–æ–≥–∏—á–Ω–æ)
    for cat_name in set(struct2.keys()) - set(struct1.keys()):
        found = False
        for other_cat in struct1.values():
            if isinstance(other_cat.get("subcategories"), dict) and cat_name in other_cat["subcategories"]:
                subcat_score = compare_categories(
                    struct2[cat_name],
                    other_cat["subcategories"][cat_name]
                )
                total_score += subcat_score * 0.25
                total_possible += 100 * 0.25
                found = True
                break
        if not found:
            total_possible += 100

    return min(100, (total_score / total_possible) * 100) if total_possible > 0 else 0

# –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
if __name__ == "__main__":
    # –ó–∞–≥—Ä—É–∑–∫–∞ JSON-—Ñ–∞–π–ª–æ–≤
    file1 = "categories1.json"
    file2 = "categories2.json"

    try:
        data1 = load_json(file1)
        data2 = load_json(file2)
    except FileNotFoundError as e:
        print(f"–û—à–∏–±–∫–∞: —Ñ–∞–π–ª –Ω–µ –Ω–∞–π–¥–µ–Ω ‚Äî {e}")
        exit()

    # –í—ã—á–∏—Å–ª–µ–Ω–∏–µ –ø—Ä–æ—Ü–µ–Ω—Ç–∞ —Å–æ–≤–ø–∞–¥–µ–Ω–∏—è
    similarity_percent = compare_category_structures(data1, data2)
    print(f"\n–ü—Ä–æ—Ü–µ–Ω—Ç —Å–æ–≤–ø–∞–¥–µ–Ω–∏—è —Å—Ç—Ä—É–∫—Ç—É—Ä –∫–∞—Ç–µ–≥–æ—Ä–∏–π: {similarity_percent:.2f}%")

    # –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –¥–µ—Ç–∞–ª–∏–∑–∞—Ü–∏—è (–æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ)
    print("\n–î–µ—Ç–∞–ª–∏–∑–∞—Ü–∏—è —Å–æ–≤–ø–∞–¥–µ–Ω–∏–π:")
    common_cats = set(data1.keys()) & set(data2.keys())
    for cat_name in common_cats:
        cat_score = compare_categories(data1[cat_name], data2[cat_name])
        print(f"- –ö–∞—Ç–µ–≥–æ—Ä–∏—è '{cat_name}': {cat_score:.1f}%")
