import java.util.stream.Collectors;

public class ReverseStringStream {
    public static String reverse(String str) {
        if (str == null) return null;
        return str.chars()
                  .mapToObj(c -> (char) c)
                  .collect(Collectors.reducing((a, b) -> b + a))
                  .orElse("");
    }

