QueueHandle_t* xQueueCreate(size_t maxItems, size_t itemSize) {
    static uint8_t queueMemory[1024]; // –°—Ç–∞—Ç–∏—á–µ—Å–∫–∏–π –±—É—Ñ–µ—Ä (–≤ —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–æ–µ–∫—Ç–µ –∏—Å–ø–æ–ª—å–∑—É–π—Ç–µ –¥–∏–Ω–∞–º–∏—á–µ—Å–∫–∏–π)
    QueueHandle_t* queue = (QueueHandle_t*)queueMemory;
    queue->queueBuffer = queueMemory + sizeof(QueueHandle_t);
    queue->itemSize = itemSize;
    queue->maxItems = maxItems;
    queue->head = 0;
    queue->tail = 0;
    queue->count = 0;
    return queue;
}

BaseType_t xQueueSend(QueueHandle_t* queue, const void* item, uint32_t ticksToWait) {
    if (queue->count >= queue->maxItems) {
        if (ticksToWait == 0) return pdFAIL;
        vTaskDelay(ticksToWait);
    }

    memcpy((uint8_t*)queue->queueBuffer + queue->head * queue->itemSize, item, queue->itemSize);
    queue->head = (queue->head + 1) % queue->maxItems;
    queue->count++;
    return pdPASS;
}

BaseType_t xQueueReceive(QueueHandle_t* queue, void* item, uint32_t ticksToWait) {
    if (queue->count == 0) {
        if (ticksToWait == 0) return pdFAIL;
        vTaskDelay(ticksToWait);
    }

    memcpy(item, (uint8_t*)queue->queueBuffer + queue->tail * queue->itemSize, queue->itemSize);
    queue->tail = (queue->tail + 1) % queue->maxItems;
    queue->count--;
    return pdPASS;
}
