## 2. –†–µ–∞–ª–∏–∑–∞—Ü–∏—è –Ω–∞ C —Å –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ–º LLVM-C API

```c
#include <llvm-c/Core.h>
#include <llvm-c/ExecutionEngine.h>
#include <llvm-c/Target.h>
#include <llvm-c/Transforms/Scalar.h>
#include <stdio.h>
#include <stdlib.h>

// –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è LLVM
void initialize_llvm() {
    LLVMInitializeNativeTarget();
    LLVMInitializeNativeAsmPrinter();
}

// –û—Å–≤–æ–±–æ–∂–¥–µ–Ω–∏–µ —Ä–µ—Å—É—Ä—Å–æ–≤
void cleanup_llvm(LLVMModuleRef module, LLVMPassManagerRef pass_manager) {
    LLVMDisposePassManager(pass_manager);
    LLVMDisposeModule(module);
}

// –°–æ–∑–¥–∞–Ω–∏–µ –º–æ–¥—É–ª—è –∏ —Ñ—É–Ω–∫—Ü–∏–∏ fib
LLVMValueRef create_fib_function(LLVMModuleRef module) {
    // 1. –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–∏–ø—ã
    LLVMTypeRef i32_type = LLVMInt32TypeInContext(LLVMGetGlobalContext());
    LLVMTypeRef func_type = LLVMFunctionType(i32_type, &i32_type, 1, 0);

    // 2. –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é
    LLVMValueRef fib_func = LLVMAddFunction(module, "fib", func_type);

    // 3. –î–æ–±–∞–≤–ª—è–µ–º –∞—Ä–≥—É–º–µ–Ω—Ç—ã
    LLVMValueRef arg_n = LLVMGetParam(fib_func, 0);
    LLVMSetParamAlignment(arg_n, 4); // –í—ã—Ä–∞–≤–Ω–∏–≤–∞–Ω–∏–µ –ø–æ 4 –±–∞–π—Ç–∞–º

    // 4. –°–æ–∑–¥–∞–µ–º –±–∞–∑–æ–≤—ã–µ –±–ª–æ–∫–∏
    LLVMBasicBlockRef entry_block = LLVMAppendBasicBlock(fib_func, "entry");
    LLVMBasicBlockRef base_case_block = LLVMAppendBasicBlock(fib_func, "base_case");
    LLVMBasicBlockRef recurse_block = LLVMAppendBasicBlock(fib_func, "recurse");
    LLVMBasicBlockRef exit_block = LLVMAppendBasicBlock(fib_func, "exit");

    // 5. –°–æ–∑–¥–∞–µ–º –±–∏–ª–¥–µ—Ä
    LLVMBuilderRef builder = LLVMCreateBuilder();

    // 6. –ù–∞—á–∏–Ω–∞–µ–º —Å entry –±–ª–æ–∫–∞
    LLVMPositionBuilderAtEnd(builder, entry_block);

    // 7. –ü—Ä–æ–≤–µ—Ä—è–µ–º —É—Å–ª–æ–≤–∏–µ n <= 1
    LLVMValueRef cond = LLVMBuildICmp(builder, LLVMIntSLE, arg_n, 
                                     LLVMConstInt(i32_type, 1, 0), "cond");
    LLVMBuildCondBr(builder, cond, base_case_block, recurse_block);

    // 8. –û–±—Ä–∞–±–æ—Ç–∫–∞ –±–∞–∑–æ–≤–æ–≥–æ —Å–ª—É—á–∞—è (n <= 1)
    LLVMPositionBuilderAtEnd(builder, base_case_block);
    LLVMBuildBr(builder, exit_block);

    // 9. –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π —Å–ª—É—á–∞–π
    LLVMPositionBuilderAtEnd(builder, recurse_block);

    // –í—ã—á–∏—Å–ª—è–µ–º n-1
    LLVMValueRef n_minus_1 = LLVMBuildSub(builder, arg_n, 
                                         LLVMConstInt(i32_type, 1, 0), "n-1");
    // –í—ã—á–∏—Å–ª—è–µ–º n-2
    LLVMValueRef n_minus_2 = LLVMBuildSub(builder, arg_n, 
                                         LLVMConstInt(i32_type, 2, 0), "n-2");

    // –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–µ –≤—ã–∑–æ–≤—ã
    LLVMValueRef fib_n_minus_1 = LLVMBuildCall(builder, fib_func, &n_minus_1, 1, "fib(n-1)");
    LLVMValueRef fib_n_minus_2 = LLVMBuildCall(builder, fib_func, &n_minus_2, 1, "fib(n-2)");

    // –°–∫–ª–∞–¥—ã–≤–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã
    LLVMValueRef sum = LLVMBuildAdd(builder, fib_n_minus_1, fib_n_minus_2, "sum");

    // –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ –≤—ã—Ö–æ–¥—É
    LLVMBuildBr(builder, exit_block);

    // 10. –í—ã—Ö–æ–¥–Ω–æ–π –±–ª–æ–∫
    LLVMPositionBuilderAtEnd(builder, exit_block);

    // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏–µ (–ª–∏–±–æ 1, –ª–∏–±–æ —Å—É–º–º—É)
    LLVMValueRef ret_val = LLVMBuildPhi(builder, i32_type, "ret_val");
    LLVMBuildRet(ret_val);

    // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º PHI —É–∑–ª—ã
    LLVMAddIncoming(ret_val, 
                   (LLVMValueRef[]){LLVMConstInt(i32_type, 1, 0), sum},
                   (LLVMBasicBlockRef[]){base_case_block, recurse_block}, 2);

    LLVMDisposeBuilder(builder);
    return fib_func;
}

// –ö–æ–º–ø–∏–ª—è—Ü–∏—è –∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–µ
int main(int argc, char **argv) {
    if (argc != 2) {
        fprintf(stderr, "Usage: %s <n>\n", argv[0]);
        return 1;
    }

    int n = atoi(argv[1]);
    if (n < 0) {
        fprintf(stderr, "Input must be non-negative\n");
        return 1;
    }

    // 1. –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è LLVM
    initialize_llvm();

    // 2. –°–æ–∑–¥–∞–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç –∏ –º–æ–¥—É–ª—å
    LLVMContextRef context = LLVMGetGlobalContext();
    LLVMModuleRef module = LLVMModuleCreateWithNameInContext("fib_module", context);

    // 3. –°–æ–∑–¥–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é fib
    LLVMValueRef fib_func = create_fib_function(module);

    // 4. –°–æ–∑–¥–∞–µ–º –º–µ–Ω–µ–¥–∂–µ—Ä –ø—Ä–æ—Ö–æ–¥–æ–≤ –¥–ª—è –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏
    LLVMPassManagerRef pass_manager = LLVMCreatePassManager();
    LLVMAddConstantPropagationPass(pass_manager);
    LLVMAddDeadCodeEliminationPass(pass_manager);
    LLVMAddInstructionCombiningPass(pass_manager);
    LLVMRunPassManager(pass_manager, module);

    // 5. –°–æ–∑–¥–∞–µ–º –¥–≤–∏–∂–æ–∫ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è
    LLVMExecutionEngineRef engine;
    char *error = NULL;
    if (LLVMCreateExecutionEngineForModule(&engine, module, &error)) {
        fprintf(stderr, "Failed to create execution engine: %s\n", error);
        LLVMDisposeMessage(error);
        return 1;
    }

    // 6. –ü–æ–ª—É—á–∞–µ–º —É–∫–∞–∑–∞—Ç–µ–ª—å –Ω–∞ —Ñ—É–Ω–∫—Ü–∏—é
    void *fib_ptr;
    if (LLVMGetFunctionAddress(engine, "fib", &fib_ptr)) {
        fprintf(stderr, "Failed to get function address\n");
        return 1;
    }

    // 7. –í—ã–∑—ã–≤–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é
    typedef int (*FibFunc)(int);
    FibFunc fib = (FibFunc)fib_ptr;
    int result = fib(n);

    printf("fib(%d) = %d\n", n, result);

    // 8. –û—Å–≤–æ–±–æ–∂–¥–∞–µ–º —Ä–µ—Å—É—Ä—Å—ã
    LLVMDisposeExecutionEngine(engine);
    cleanup_llvm(module, pass_manager);

    return 0;
}
```

