### –ö–æ–¥ –ø—Ä–æ–≥—Ä–∞–º–º—ã:
```java
import java.util.Scanner;

class Fraction {
    private int numerator;   // –ß–∏—Å–ª–∏—Ç–µ–ª—å
    private int denominator; // –ó–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å

    // –ö–æ–Ω—Å—Ç—Ä—É–∫—Ç–æ—Ä (–∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Å–æ–∫—Ä–∞—â–∞–µ—Ç –¥—Ä–æ–±—å)
    public Fraction(int numerator, int denominator) {
        if (denominator == 0) {
            throw new IllegalArgumentException("–ó–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –Ω–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –Ω—É–ª—ë–º!");
        }
        this.denominator = denominator;
        this.numerator = numerator;
        reduce();
    }

    // –°–æ–∫—Ä–∞—â–µ–Ω–∏–µ –¥—Ä–æ–±–∏ —Å –ø–æ–º–æ—â—å—é –ù–û–î
    private void reduce() {
        int gcd = gcd(Math.abs(numerator), Math.abs(denominator));
        numerator /= gcd;
        denominator /= gcd;

        // –î–µ–ª–∞–µ–º –∑–Ω–∞–º–µ–Ω–∞—Ç–µ–ª—å –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω—ã–º
        if (denominator < 0) {
            numerator *= -1;
            denominator *= -1;
        }
    }

    // –ù–∞—Ö–æ–∂–¥–µ–Ω–∏–µ –ù–û–î (–∞–ª–≥–æ—Ä–∏—Ç–º –ï–≤–∫–ª–∏–¥–∞)
    private static int gcd(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }

    // –°–ª–æ–∂–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    public Fraction add(Fraction other) {
        int newNumerator = this.numerator * other.denominator + other.numerator * this.denominator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    // –í—ã—á–∏—Ç–∞–Ω–∏–µ –¥—Ä–æ–±–µ–π
    public Fraction subtract(Fraction other) {
        int newNumerator = this.numerator * other.denominator - other.numerator * this.denominator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    // –£–º–Ω–æ–∂–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    public Fraction multiply(Fraction other) {
        int newNumerator = this.numerator * other.numerator;
        int newDenominator = this.denominator * other.denominator;
        return new Fraction(newNumerator, newDenominator);
    }

    // –î–µ–ª–µ–Ω–∏–µ –¥—Ä–æ–±–µ–π
    public Fraction divide(Fraction other) {
        if (other.numerator == 0) {
            throw new ArithmeticException("–î–µ–ª–µ–Ω–∏–µ –Ω–∞ –Ω–æ–ª—å!");
        }
        int newNumerator = this.numerator * other.denominator;
        int newDenominator = this.denominator * other.numerator;
        return new Fraction(newNumerator, newDenominator);
    }

    // –ü—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏–µ –≤ —Å—Ç—Ä–æ–∫—É (–Ω–∞–ø—Ä–∏–º–µ—Ä, "3/4" –∏–ª–∏ "2")
    @Override
    public String toString() {
        if (denominator == 1) {
            return Integer.toString(numerator);
        } else {
            return numerator + "/" + denominator;
        }
    }
}

public class FractionCalculator {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.println("–ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –¥—Ä–æ–±–µ–π");
        System.out.println("–í–≤–µ–¥–∏—Ç–µ –¥—Ä–æ–±—å –≤ —Ñ–æ—Ä–º–∞—Ç–µ 'a/b' (–Ω–∞–ø—Ä–∏–º–µ—Ä, 3/4 –∏–ª–∏ 5/1):");

        try {
            // –í–≤–æ–¥ –ø–µ—Ä–≤–æ–π –¥—Ä–æ–±–∏
            System.out.print("–ü–µ—Ä–≤–∞—è –¥—Ä–æ–±—å: ");
            String input1 = scanner.nextLine();
            Fraction frac1 = parseFraction(input1);

            // –í–≤–æ–¥ –≤—Ç–æ—Ä–æ–π –¥—Ä–æ–±–∏
            System.out.print("–í—Ç–æ—Ä–∞—è –¥—Ä–æ–±—å: ");
            String input2 = scanner.nextLine();
            Fraction frac2 = parseFraction(input2);

            // –í—ã–±–æ—Ä –æ–ø–µ—Ä–∞—Ü–∏–∏
            System.out.println("–í—ã–±–µ—Ä–∏—Ç–µ –æ–ø–µ—Ä–∞—Ü–∏—é (+, -, *, /):");
            char operation = scanner.next().charAt(0);

            Fraction result = null;
            switch (operation) {
                case '+':
                    result = frac1.add(frac2);
                    break;
                case '-':
                    result = frac1.subtract(frac2);
                    break;
                case '*':
                    result = frac1.multiply(frac2);
                    break;
                case '/':
                    result = frac1.divide(frac2);
                    break;
                default:
                    System.out.println("–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ–ø–µ—Ä–∞—Ü–∏—è!");
                    return;
            }

            System.out.println("–†–µ–∑—É–ª—å—Ç–∞—Ç: " + result);

        } catch (IllegalArgumentException | NumberFormatException e) {
            System.out.println("–û—à–∏–±–∫–∞ –≤–≤–æ–¥–∞: " + e.getMessage());
        } finally {
            scanner.close();
        }
    }

    // –ü–∞—Ä—Å–∏–Ω–≥ —Å—Ç—Ä–æ–∫–∏ –≤ –¥—Ä–æ–±—å
    private static Fraction parseFraction(String input) {
        input = input.trim();
        if (input.contains("/")) {
            String[] parts = input.split("/");
            if (parts.length != 2) {
                throw new IllegalArgumentException("–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥—Ä–æ–±–∏! –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ 'a/b'.");
            }
            int numerator = Integer.parseInt(parts[0]);
            int denominator = Integer.parseInt(parts[1]);
            return new Fraction(numerator, denominator);
        } else {
            int numerator = Integer.parseInt(input);
            return new Fraction(numerator, 1);
        }
    }
}
