import json
from datetime import datetime
import os

class BankAccount:
    def __init__(self, account_number, owner_name, initial_balance=0):
        self.account_number = account_number
        self.owner_name = owner_name
        self.balance = initial_balance
        self.transaction_history = []

    def deposit(self, amount):
        if amount <= 0:
            raise ValueError("–°—É–º–º–∞ –ø–æ–ø–æ–ª–Ω–µ–Ω–∏—è –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω–æ–π")
        self.balance += amount
        self._add_transaction("–ü–æ–ø–æ–ª–Ω–µ–Ω–∏–µ", amount)
        return self.balance

    def withdraw(self, amount):
        if amount <= 0:
            raise ValueError("–°—É–º–º–∞ —Å–Ω—è—Ç–∏—è –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –ø–æ–ª–æ–∂–∏—Ç–µ–ª—å–Ω–æ–π")
        if amount > self.balance:
            raise ValueError("–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤ –Ω–∞ —Å—á–µ—Ç–µ")
        self.balance -= amount
        self._add_transaction("–°–Ω—è—Ç–∏–µ", -amount)
        return self.balance

    def _add_transaction(self, transaction_type, amount):
        transaction = {
            "date": datetime.now().strftime("%Y-%m-%d %H:%M:%S"),
            "type": transaction_type,
            "amount": amount,
            "balance_after": self.balance
        }
        self.transaction_history.append(transaction)

    def get_balance(self):
        return self.balance

    def get_transaction_history(self):
        return self.transaction_history.copy()

    def to_dict(self):
        return {
            "account_number": self.account_number,
            "owner_name": self.owner_name,
            "balance": self.balance,
            "transaction_history": self.transaction_history
        }

    @classmethod
    def from_dict(cls, data):
        account = cls(
            data["account_number"],
            data["owner_name"],
            data["balance"]
        )
        account.transaction_history = data["transaction_history"]
        return account


class BankApp:
    def __init__(self, data_file="bank_data.json"):
        self.data_file = data_file
        self.accounts = {}
        self.load_data()

    def load_data(self):
        if os.path.exists(self.data_file):
            with open(self.data_file, 'r') as f:
                try:
                    data = json.load(f)
                    for acc_num, acc_data in data.items():
                        self.accounts[acc_num] = BankAccount.from_dict(acc_data)
                except json.JSONDecodeError:
                    print("–û—à–∏–±–∫–∞ —á—Ç–µ–Ω–∏—è —Ñ–∞–π–ª–∞ –¥–∞–Ω–Ω—ã—Ö. –°–æ–∑–¥–∞–µ—Ç—Å—è –Ω–æ–≤—ã–π —Ñ–∞–π–ª.")
                    self.accounts = {}
        else:
            self.accounts = {}

    def save_data(self):
        data = {acc_num: acc.to_dict() for acc_num, acc in self.accounts.items()}
        with open(self.data_file, 'w') as f:
            json.dump(data, f, indent=2, ensure_ascii=False)

    def create_account(self, owner_name, initial_balance=0):
        account_number = str(len(self.accounts) + 1).zfill(6)  # 6-–∑–Ω–∞—á–Ω—ã–π –Ω–æ–º–µ—Ä —Å—á–µ—Ç–∞
        new_account = BankAccount(account_number, owner_name, initial_balance)
        self.accounts[account_number] = new_account
        self.save_data()
        return account_number

    def get_account(self, account_number):
        return self.accounts.get(account_number)

    def run(self):
        while True:
            print("\n=== –ë–∞–Ω–∫–æ–≤—Å–∫–æ–µ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ ===")
            print("1. –°–æ–∑–¥–∞—Ç—å —Å—á–µ—Ç")
            print("2. –í–æ–π—Ç–∏ –≤ —Å—á–µ—Ç")
            print("3. –í—ã–π—Ç–∏")

            choice = input("–í—ã–±–µ—Ä–∏—Ç–µ –¥–µ–π—Å—Ç–≤–∏–µ (1-3): ")

            if choice == "1":
                name = input("–í–≤–µ–¥–∏—Ç–µ –≤–∞—à–µ –∏–º—è: ")
                try:
                    balance = float(input("–ù–∞—á–∞–ª—å–Ω—ã–π –±–∞–ª–∞–Ω—Å (–ø–æ —É–º–æ–ª—á–∞–Ω–∏—é 0): ") or 0)
                    acc_num = self.create_account(name, balance)
                    print(f"\n–°—á–µ—Ç —É—Å–ø–µ—à–Ω–æ —Å–æ–∑–¥–∞–Ω! –ù–æ–º–µ—Ä —Å—á–µ—Ç–∞: {acc_num}")
                except ValueError:
                    print("–û—à–∏–±–∫–∞: –í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–µ —á–∏—Å–ª–æ –¥–ª—è –Ω–∞—á–∞–ª—å–Ω–æ–≥–æ –±–∞–ª–∞–Ω—Å–∞")

            elif choice == "2":
                acc_num = input("–í–≤–µ–¥–∏—Ç–µ –Ω–æ–º–µ—Ä —Å—á–µ—Ç–∞: ")
                account = self.get_account(acc_num)

                if not account:
                    print("–°—á–µ—Ç –Ω–µ –Ω–∞–π–¥–µ–Ω!")
                    continue

                print(f"\n–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å, {account.owner_name}!")
                print(f"–¢–µ–∫—É—â–∏–π –±–∞–ª–∞–Ω—Å: {account.get_balance():.2f} —Ä—É–±.")

                while True:
                    print("\n1. –ü–æ–ø–æ–ª–Ω–∏—Ç—å —Å—á–µ—Ç")
                    print("2. –°–Ω—è—Ç—å —Å—Ä–µ–¥—Å—Ç–≤–∞")
                    print("3. –ü–æ–∫–∞–∑–∞—Ç—å –±–∞–ª–∞–Ω—Å")
                    print("4. –ò—Å—Ç–æ—Ä–∏—è —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π")
                    print("5. –í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é")

                    sub_choice = input("–í—ã–±–µ—Ä–∏—Ç–µ –¥–µ–π—Å—Ç–≤–∏–µ (1-5): ")

                    if sub_choice == "1":
                        try:
                            amount = float(input("–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –¥–ª—è –ø–æ–ø–æ–ª–Ω–µ–Ω–∏—è: "))
                            new_balance = account.deposit(amount)
                            print(f"–°—á–µ—Ç –ø–æ–ø–æ–ª–Ω–µ–Ω. –ù–æ–≤—ã–π –±–∞–ª–∞–Ω—Å: {new_balance:.2f} —Ä—É–±.")
                            self.save_data()
                        except ValueError as e:
                            print(f"–û—à–∏–±–∫–∞: {e}")

                    elif sub_choice == "2":
                        try:
                            amount = float(input("–í–≤–µ–¥–∏—Ç–µ —Å—É–º–º—É –¥–ª—è —Å–Ω—è—Ç–∏—è: "))
                            new_balance = account.withdraw(amount)
                            print(f"–°—Ä–µ–¥—Å—Ç–≤–∞ —Å–Ω—è—Ç—ã. –ù–æ–≤—ã–π –±–∞–ª–∞–Ω—Å: {new_balance:.2f} —Ä—É–±.")
                            self.save_data()
                        except ValueError as e:
                            print(f"–û—à–∏–±–∫–∞: {e}")

                    elif sub_choice == "3":
                        print(f"–¢–µ–∫—É—â–∏–π –±–∞–ª–∞–Ω—Å: {account.get_balance():.2f} —Ä—É–±.")

                    elif sub_choice == "4":
                        print("\n–ò—Å—Ç–æ—Ä–∏—è —Ç—Ä–∞–Ω–∑–∞–∫—Ü–∏–π:")
                        for tx in account.get_transaction_history():
                            print(f"{tx['date']} | {tx['type']} | {tx['amount']:+.2f} | –ë–∞–ª–∞–Ω—Å: {tx['balance_after']:.2f}")

                    elif sub_choice == "5":
                        break

                    else:
                        print("–ù–µ–≤–µ—Ä–Ω—ã–π –≤—ã–±–æ—Ä. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.")

            elif choice == "3":
                print("–î–æ —Å–≤–∏–¥–∞–Ω–∏—è!")
                break

            else:
                print("–ù–µ–≤–µ—Ä–Ω—ã–π –≤—ã–±–æ—Ä. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.")


if __name__ == "__main__":
    app = BankApp()
    app.run()
```

