using System;

class Program
{
    static void Main()
    {
        double pi = Math.PI;
        Console.WriteLine($"œÄ (Math.PI): {pi}");

        // –í—ã–≤–æ–¥–∏–º —Ç–æ–ª—å–∫–æ –¥—Ä–æ–±–Ω—É—é —á–∞—Å—Ç—å
